    function handleCards(obj){
    	cardInfo = $(obj);

        cardInfo.toggleClass('open-block');

        if( cardInfo.hasClass('open-block') ){
            $("a", obj).attr('tabindex', 0);
            $(".acc-trigger", obj).attr('aria-expanded', 'true');
            $('i', obj).addClass('point-left');
            cardInfo.attr('aria-hidden', 'false');
        }else{
            $("a", obj).attr('tabindex', -1);
            $(".acc-trigger", obj).attr('aria-expanded', 'false');
            $('i', obj).removeClass('point-left');
            cardInfo.attr('aria-hidden', 'true');
        }
    }

	function searchText() {
		var defSearchText = "Search..."

	    var searchBox = $('#swsitesearch-txt-searchstring');
        searchBox.attr("aria-label", "Search input");
		searchBox.attr("placeholder", '');
        searchBox.val(defSearchText);
		searchBox.focus(function() {
			if($(this).val() == defSearchText) {
				$(this).val("");
			}
		});
		searchBox.blur(function() {
			if($(this).val() == "") {
				$(this).val(defSearchText);
			}
		});
	}

	function calendarListView(){
		//DOC auto click function for schoolwires calendar list view for small mobile devices
		if(!$(".ui-widget.app.calendar #calendarlist-pnl-specialview.loaded").length){
			$("a.ui-btn-toggle.list").click();
			$("a.ui-btn-toggle.month").click();
			$(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("loaded");
		}

		var getWidth = $(window).width();
		if(getWidth >= 640){ // 640+
			$(".ui-widget.app.calendar #calendar").removeClass("ui-helper-hidden");
			$(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("ui-helper-hidden");
		} if(getWidth < 640){ // 480-
			$(".ui-widget.app.calendar #calendar").addClass("ui-helper-hidden");
			$(".ui-widget.app.calendar #calendarlist-pnl-specialview").removeClass("ui-helper-hidden");
			$(".ui-btn-toggle.month").removeClass("active");
			$(".ui-btn-toggle.list").addClass("active");
		}
	}

    function clearDropdowns(){
    	$('.custom-dropdown.openMenu input, .custom-dropdown.openMenu a, .custom-dropdown.openMenu .custom-dropdown > button').attr('tabindex', -1);
        $('.custom-dropdown.openMenu .dropdown').attr('aria-expanded', 'false').attr('aria-hidden', 'true').slideUp();
        $('.custom-dropdown.openMenu .dropdown-icon').removeClass('point-up').addClass('point-right');
        $('.custom-dropdown.openMenu > button').removeClass('acc-open');
        $('.custom-dropdown.openMenu li').removeClass('active-list-item');
		dropdownKeyboardFunctionality($('.custom-dropdown.openMenu .dropdown'), true);
		$('.custom-dropdown.openMenu').removeClass("openMenu");
    }

    function dropdownKeyboardFunctionality(dropdown, clear){
    	if( clear ){
        	dropdown.off('keydown');
        }else{
        	$('input, a', dropdown).attr('tabindex', 0);
	        $('li:first', dropdown).addClass('active-list-item');
        	$('li.active-list-item a', dropdown).trigger('focus');

            	// 38 = up and 40 = down
        	dropdown.keydown(function(e){
            	if(e.which == 38 || e.which == 40){
                	e.preventDefault();
                    switch( e.which ){
                        case 38:  //up
							if( $('li.active-list-item', this).index() == 0){
                            	$(this).siblings('button').focus();
                                $('li', this).removeClass('active-list-item');
                                $('li:first', this).addClass('active-list-item');
                            }else{	
                            	var topItem = $('li.active-list-item', this).prev('li');
                                if(topItem.hasClass('list-label')) topItem = topItem.prev('li');

                                $('li.active-list-item', this).removeClass('active-list-item');
                                topItem.addClass('active-list-item');
                                $('a', topItem).focus();
                            }
                            break;
                        case 40:  //down
                        	if( $('li.active-list-item', this).index() == $('li', this).length - 1 ){
                                $('li', this).removeClass('active-list-item');
                                $('li:first', this).addClass('active-list-item');
                                $('li.active-list-item a', this).focus();
                            }else{
                            	var bottomItem = $('li.active-list-item', this).next('li');
                                if(bottomItem.hasClass('list-label')) bottomItem = bottomItem.next('li');

                                $('li.active-list-item', this).removeClass('active-list-item');
                                $(bottomItem).addClass('active-list-item');
                                $('a', bottomItem).focus();
                            }
                            break;
                        default:
                            //pass
                            break;
                    }
                }
            });
        }
    }

	/* customDropdowns
	**  Use: Sets up custom dropdown menus.
	**  Param: openIcon, closedIcon -- The classes that you want to mark the on/off state of the menu icons (dont pass the . or #)
	*/
    var dropdownTimeout = ""; //Will hold our timeout object that will ge t passed among the dropdown functions.
	function customDropdowns(openIcon, closedIcon){
        $('.custom-dropdown input, .custom-dropdown a, .custom-dropdown .custom-dropdown > button').attr('tabindex', -1);
        $('.custom-dropdown button').on('keydown', function(e) { // Trigger the click event from the keyboard
            var code = e.which;
            // 13 = Return, 32 = Space
           	if ((code === 13) || (code === 32)) {
                e.preventDefault();

                $(this).click();
                if( !$(this).parent().hasClass('openMenu') ){
                	//clear our keyboard hooks since the menu is closing.
                	dropdownKeyboardFunctionality($('~ .dropdown', this), true);
                }else{
                	//add our keyboard hooks
               		dropdownKeyboardFunctionality($('~ .dropdown', this));
                }


            }
        });

		//Custom Dropdown Functionality
		$('.custom-dropdown').off('click').click(function(e){
			clearTimeout(dropdownTimeout);

			if( !$(this).hasClass("openMenu") ){
				clearDropdowns();
				$('.dropdown', this).attr('aria-expanded', 'true').attr('aria-hidden', 'false').slideDown();
				$(this).addClass("openMenu");
                $('.custom-dropdown.openMenu input, .custom-dropdown.openMenu a, .custom-dropdown.openMenu .custom-dropdown > button').attr('tabindex', 0);
				$('.dropdown-icon', this).removeClass(closedIcon).addClass(openIcon);
			}else{
            	clearDropdowns();
			}
		})
		.hover(function(e){ //In
			clearTimeout(dropdownTimeout);
		},function(e){ //Out
			//canDropDownHide = true;
			dropdownTimeout = setTimeout(function(){
            	clearDropdowns();
			}, 800);
		});
	}

	/*
	** smoothScroll
	** Use: This will smoothly scroll to the id that was passed to the function.
	** Return: N/A
	** Parameter(s): to (jQuery "#id") The id of the section to be scolled to.
	** 		offset (int) The amount of space you want to be left on the top of the element after scroll (in a whole number int value)
	*/
	function smoothScroll( to, offset ){
	    if( to.indexOf("#") < 0 ){ to = "#" + to; } //makes sure we have a valid id accessor string
	    var target = $(to);

	    if (target.length) {
	        $('html,body').animate({
	            scrollTop: target.offset().top - offset //this offset accounts for the fixed menu on the top
	        }, 800, function(){
	            $("html, body").stop(true, false);
	        });
	        return false;
	    }
	}


    function fitTextSetup(){
    	//$('#hp-link-cards-cont .card-title-slide h1').fitText(1.2, {minFontSize: '30px', maxFontSize: '50px;'});

        $('#hp-stats .stat-number').fitText(0.26, {minFontSize: '70px', maxFontSize: '90px'});
        $('#hp-stats .stat-label').fitText(0.26, {minFontSize: '21px', maxFontSize: '26px'});
        $('#hp-cards .card-title').fitText(1.7, {minFontSize: '21px', maxFontSize: '30px'});
    }

    /*global jQuery */
    /*!
    * FitText.js 1.2
    *
    * Copyright 2011, Dave Rupert http://daverupert.com
    * Released under the WTFPL license
    * http://sam.zoy.org/wtfpl/
    *
    * Date: Thu May 05 14:23:00 2011 -0600
    */

    (function( $ ){

      $.fn.fitText = function( kompressor, options ) {

        // Setup options
        var compressor = kompressor || 1,
            settings = $.extend({
              'minFontSize' : Number.NEGATIVE_INFINITY,
              'maxFontSize' : Number.POSITIVE_INFINITY
            }, options);

        return this.each(function(){

          // Store the object
          var $this = $(this);

          // Resizer() resizes items based on the object width divided by the compressor * 10
          var resizer = function () {
            $this.css('font-size', Math.max(Math.min($this.width() / (compressor*10), parseFloat(settings.maxFontSize)), parseFloat(settings.minFontSize)));
          };

          // Call once to set.
          resizer();

          // Call on resize. Opera debounces their resize by default.
          $(window).on('resize.fittext orientationchange.fittext', resizer);

        });

      };

    })( jQuery );

	//loads the IFrame Player API code asynchronously.
	var tag = document.createElement('script');
	tag.src = "//www.youtube.com/iframe_api";
	var firstScriptTag = document.getElementsByTagName('script')[0];
	firstScriptTag.parentNode.insertBefore(tag, firstScriptTag);

	//creates an iframe (and YouTube player) after the API code downloads.
	var player;
	var windowWidth;
	var windowHeight;
	function onYouTubeIframeAPIReady() {
		if( !false ){
			windowWidth = $(window).width();
			windowHeight = $(window).height();
            if( typeof(YT) != 'undefined' || typeof(YT.Player) != 'undefined' ){
                player = new YT.Player('hp-streaming-video', {
                    height: windowHeight,
                    width: windowWidth,
                    videoId: 'FMKJuo5gztQ',
                    playerVars: { 
                        'modestbranding': 1,
                        'controls': 0, 
                        'autoplay': 1,
                        'rel' : 0,
                        'loop': 1,
                        'wmode': 'opaque'
                    },
                    events: {
                        'onReady': onPlayerReady,
                        'onStateChange': onPlayerStateChange
                    }
                });
        	}
		}else{
			$('#hp-slideshow').removeClass('hidden');
		}
	}

	//the API will call this function when the video player is ready.
	function onPlayerReady(event) {
		csVideoResize();
        player.mute()
		$(window).resize(csVideoResize);
	}

	function onPlayerStateChange(event){ //this makes sure the video will always play and loop
		if( event.data === 0 ) player.playVideo();
	}

	function csVideoResize(){
		if($("#hp-streaming-video").length) {
			var screenWidth = $(window).width();
			var screenHeight = $(window).height();
			var screenRatio = screenHeight / screenWidth;
			var containerHeight = screenWidth * screenRatio;
			var vidWidth = screenWidth, vidHeight = 0;

			// GET ITEMS RATIO
			var initW = 1500, initH = 915;

            if( screenWidth < 1024 ){
            	initW = 768;
                initH = 435;
            }

			var ratio = initH / initW;
			vidHeight = vidWidth * ratio;

			if(vidHeight < screenHeight && screenWidth >= 1024){
				vidHeight = screenHeight;
				vidWidth = vidHeight / ratio;
			}else if( screenWidth < 1024 ){
            	screenHeight = vidHeight;
            }

            var centerWidth = (vidWidth - screenWidth) / 2;
            var centerHeight = (vidHeight - screenHeight) / 2;
			// APPLY THE DIMENSIONS
			$("#hp-streaming-video").css({"width": vidWidth + "px", "height": vidHeight + "px", "margin": "-" + centerHeight + "px 0px 0px -" + centerWidth + "px"});

            $('#hp-streaming-video-outer').css('height', screenHeight);
		}
	}

    function clearLogo(){
    	var modifier = 27;

    	$(".clear-logo").stop(true, true).animate({'padding-left': $('#gb-logo .banner').outerWidth() + modifier}, 600);
    }

    /*stickyHeader
		Use: It handles the sticky header that will make the header become fixed upon scrolling
		Parameter: shutoff -- this is a numeric value in which the width of the screen will stop the sticky header from activating.
	*/
	var csNavIsSticky = 0;
	function stickyHeader(shutoff, scroll){
		var getWidth = $(window).width();

		if (getWidth >= 1024) {
			// CHANNEL BAR
			var gbNavOffSet = $("#gb-sticky-header-holder").offset().top;

			if ($(window).scrollTop() <= gbNavOffSet) {
				if(csNavIsSticky) {
					$("#gb-sticky-header").removeClass("on");
					$("#gb-sticky-header-holder").css("height", "auto");
					csNavIsSticky = 0;
				}
			} else {
				$("#gb-sticky-header-holder").css("height", $("#gb-sticky-header").outerHeight());				
				if(!csNavIsSticky) {
					$("#gb-sticky-header").addClass("on");
					csNavIsSticky = 1;
				}
			}

            if($(".sp").length && $(".ui-widget.app.calendar").length) {
                if($(".wcm-controls").hasClass("wcm-stuck")) {
                    $(".wcm-controls").css("margin-top", $("#gb-sticky-header").outerHeight(true));
                }
            }
		} else {
			if(csNavIsSticky == 1) {
				$("#gb-sticky-header").removeClass("on");
				$("#gb-sticky-header-holder").css("height", "auto");

				csNavIsSticky = 0;
			}
		}
	}

