$(document).ready(function(){
   var beaconURL='https://analytics.schoolwires.com/analytics.asmx/Insert?AccountNumber=%2bBZcNY4AkrC6v%2bcO8hEczQ%3d%3d&SessionID=9e8fa9f4-8746-4c91-80e6-32e21c2dfe65&SiteID=8&ChannelID=0&SectionID=8&PageID=9&HitDate=4%2f29%2f2020+11%3a39%3a03+AM&Browser=Unknown+0.0&OS=Unknown&IPAddress=10.61.84.227';
