	function pgScreenSize(){
		var getWidth = $(window).width();
		if (getWidth >= 1024) { // 960+
			photoGallery();
        		$(window).scroll(topScroll);
		} if (getWidth >= 768 && getWidth < 1024) { // 768+
			photoGallery();
		} if (getWidth >= 640 && getWidth < 768) { // 640+
			photoGallery();
		} if (getWidth >= 480 && getWidth < 640) { // 480+
			photoGallery();
			appView();
		} if (getWidth >= 320 && getWidth < 480) { // 320+
			photoGallery();
			appView();
		}
	}

	function photoGallery(){
		if(!$("#sw-content-container10.region.ui-hp .rs-gallery-container").length){
			// PHOTO GALLERY CONFIGURATION
			$("#sw-content-container10.ui-hp div.ui-widget.app.cs-rs-multimedia-rotator").csRsMultimediaRotator({
		                "imageWidth"              : 1280,
		                "imageHeight"             : 533,
		                "appHeader"               : "no",
		                "titles"                  : "yes",
		                "captions"                : "yes",
		                "galleryOverlay"          : "no",
		                "linkedElement"           : ["all"], // ["image", "title", "overlay", "all"]
		                "playPause"               : "no",
		                "mainControls"            : "yes",
		                "contMoreLink"            : "no",
		                "contMoreLinkText"        : "Read More",
		                "descMoreLink"            : "no",
		                "descMoreLinkText"        : "Read More",
		                "bullets"                 : "yes",
		                "bulletControls"          : "no",
		                "thumbnails"              : "no",
		                "thumbnailWidth"          : 122,
		                "thumbnailHeight"         : 53,
		                "thumbnailSlideDirection" : "horizontal", // horizontal or vertical
		                "hoverPause"              : "yes",
		                "fullScreenRotator"       : "no", // image dimensions work well at 1500x915
		                "fullScreenVideo"         : "no",
		                "transitionType"          : "fade", // fade or slide
		                "transitionSpeed"         : 3,
		                "transitionDelay"         : 8,
		                "onImageLoad"             : function() { },
		                "allImagesLoaded"         : function() { },
		                "onThumbnailLoad"         : function() { },
		                "allThumbnailsLoaded"     : function() { },
		                "onTransitionStart"       : function() { },
		                "onTransitionEnd"         : function() { },
		                "watchVidClick"           : function() { },
		                "closeVidClick"           : function() { },
		                "allLoaded"               : function() { 
		                					$("#hp-slideshow .rs-gallery-container .rs-gallery-control.back").html("l");
								$("#hp-slideshow .rs-gallery-container .rs-gallery-control.next").html("r");
		                }
		        });
		}
	}

	function appView(){
		//DOC make homepage apps collapsible for small mobile devices
		if(!$("div.ui-widget.app.active").length){
			$("#hp-content div.ui-widget.app:first").addClass("active");
		}
		$("div.ui-hp div.ui-widget-header").click(function(){
			var clickedApp = $(this).parent().parent().attr("id");

			$("div.ui-widget.app").removeClass("active");
			$(this).parent().addClass("active");

			$("html, body").animate({
				scrollTop: $("#" + clickedApp).offset().top
			}, 500, function(){
				$("html, body").stop(true, false);
			});
		});
	}	

	function sameHeight(selector) {
		selector.height(function(){
			$(this).height($(this).parent().height());
		});

		selector.children().resize(function(){
			selector.height("auto");
			var screenWidth = $(window).width();
			if (screenWidth >= 1024) {
				selector.height(function(){
					$(this).height($(this).parent().height());
				});	
			}
		});
	}

	function useChannelForSection(){
		//DOC use channel name for pagelistnavigation header if one is not present
		if(!$("div.sp.column.one .ui-widget-header h1").length > 0) {
			$("div.sp.column.one .ui-widget-header").append("
");
		}
        }

	function dynamicContent(){
		// ********** MYSTART ********** //

		$(".sw-mystart-button.home a").html("NPS Home")
		$(".sw-mystart-dropdown.schoollist .selector, .sw-mystart-dropdown.schoollist .sw-dropdown-selected").text("OUR SCHOOLS");
		//$("#sw-mystart-search").appendTo("#gb-search-bottom");
		$(".sw-mystart-dropdown.schoollist").addClass("hide640");


		var mystartBtns = "
" + $(".sw-mystart-button.signin").html() + "";
			if($(".sw-mystart-button.register").length){
				mystartBtns += "
" + $(".sw-mystart-button.register").html() + "";
			}
			$("#gb-mystart-right").append('

4' + mystartBtns + '');



		$("#sw-mystart-search").unbind();	

		// ********** HEADER ********** //
		var siteName = "Newton Public Schools";
		siteName = siteName.split(" ");
		var siteNameLength = siteName.length;
		if(siteNameLength > 2){
			siteNameEnd = jQuery.trim(siteName.splice(-2, siteName.length).toString().replace(/,/g, " "));
		} else {
			siteNameEnd = jQuery.trim(siteName.splice(-1, siteName.length).toString().replace(/,/g, " "));
		}
		siteNameBegin = jQuery.trim(siteName.toString().replace(/,/g, " "));
		$("#gb-sitename").prepend("
" + siteNameEnd + "");
		$("#gb-sitename").prepend("
" + siteNameBegin + "");

		// ********** CHANNEL BAR ********** //
		//DOC add first class to first channel item
		$("li.sw-channel-item").first().addClass("first");
		//DOC add last class to last channel item
		$("li.sw-channel-item").last().addClass("last");

		// ********** BODY ********** //
		//DOC rename first breadcrumb
		$("li.ui-breadcrumb-first > a > span").text("Home");
		//DOC replace more-link text
		$("a.more-link span").text("More ");
		//DOC auto-focus username signin field
		$("#swsignin-txt-username").focus();
		//DOC center signed out message and signin button
        	if($("div.ui-spn > div > p > span").text() == "You have been signed out."){
        		$("div.ui-spn > div").css({"text-align" : "center", "padding" : "50px 0px 50px 0px"});
        		//DOC add signed out breadcrumb
        		$(".ui-breadcrumbs").append("
Signed Out");
        	}

        	$(".ui-widget.app.headlines .app-level-social-rss a.ui-btn-toolbar.rss span").text("SUBSCRIBE");

        	//DOC add sign in breadcrumb
        	if($("#swlogin").length){
        		$(".ui-breadcrumbs").append("
Sign In");
        	}
        	//MOD EVENTS
		$(".ui-hp .upcomingevents").modEvents({
			columns:"yes",
			monthLong:"yes"
		});
		eventsByDay(".ui-hp .upcomingevents .ui-articles");

		//DOC add last class to last upcoming events item
		$(".ui-widget.app.upcomingevents .ui-widget-detail .ui-articles li").last().addClass("last");

		//RESPONSIVE SEARCH
		$(".gb-search-button").click(function(e){
		    e.preventDefault();
		    var searchForm = $(this).parent();
		    window.location.href = "/site/Default.aspx?PageType=6&SiteID=4&SearchString=" + $(".gb-search-input", searchForm).val();
		});
		$(".gb-search-form").submit(function(e){
		    e.preventDefault();
		    var searchForm = $(this).parent();
		    window.location.href = "/site/Default.aspx?PageType=6&SiteID=4&SearchString=" + $(".gb-search-input", searchForm).val();
		});
		$(".gb-search-input").focus(function() {
		    if($(this).val() == "Search") {
		        $(this).val("");
		    }
		});
		$(".gb-search-input").blur(function() {
		    if($(this).val() == "") {
		        $(this).val("Search");
		    }
		});

		//CLICK FUNCTION FOR SEARCH DROPDOWN
		$("#gb-global-icons .icn.search").click(function(e){
			var getWidth = $(window).width();
			if (getWidth >= 1024) {
				e.preventDefault();
				if($(this).hasClass("closed")) {
					$("#gb-search-bottom").animate({
						        marginRight: "258px"
						    }, 400)

					$(this).removeClass("closed").addClass("open");	
					//$("#gb-search-bottom").removeClass("closed").addClass("open");			    
				} else {
					$("#gb-search-bottom").animate({
						        marginRight: "0px"
						    }, 400)
					$(this).removeClass("open").addClass("closed");	
					//$("#gb-search-bottom").removeClass("open").addClass("closed");	
				}
			} else {
				e.preventDefault();
				if($(this).hasClass("closed")) {
					$("#gb-search-bottom").animate({
						        marginTop: "-45px"
						    }, 400)

					$(this).removeClass("closed").addClass("open");	
					//$("#gb-search-bottom").removeClass("closed").addClass("open");			    
				} else {
					$("#gb-search-bottom").animate({
						        marginTop: "0px"
						    }, 400)
					$(this).removeClass("open").addClass("closed");	
					//$("#gb-search-bottom").removeClass("open").addClass("closed");	
				}
			}				    
		});

		// ********** FOOTER ********** //
		$("#back-to-top").click(function(e){
        		e.preventDefault();
        		$("html, body").animate({
				scrollTop: $("body").offset().top
			}, 500, function(){
				$("html, body").stop(true, false);
			});
        	});




		$("#gb-global-icons .icn.top").click(function(e){
	        		e.preventDefault();
	        		$("html, body").animate({
				scrollTop: $("body").offset().top
				}, 500, function(){
				$("html, body").stop(true, false);
			});		
        	});


            $("#gb-global-icons .icn.sm").click(function(e){
            	e.preventDefault();
                $("#gb-global-icons #sm-container").slideToggle("slow");
            });
        	//SET SITEMAP LINK
        	var sitemapLink = $(".gb-footer-one-b h1.sitemap a").attr("href");
		$(".gb-footer-one-b a.sitemap-link").attr("href", sitemapLink);

		//DOC get Schoolwires footer links
		var feedbackURL = $("div#sw-footer-links li:eq(0) a").attr("href");
		var termsURL = $("div#sw-footer-links li:eq(2) a").attr("href");
		var policyURL = $("div#sw-footer-links li:eq(1) a").attr("href");

		$("a#feedback").attr("href", feedbackURL);
		$("a#terms").attr("href", termsURL);
		$("a#policy").attr("href", policyURL);			
	}

	var topScroll = function(){
		// BACK TO TOP BUTTON
		var pageH = $("#gb-page").height() / 3;

		// SHOW OR HIDE BACK TO TOP BUTTON
		if($(window).scrollTop() < pageH) {
			$("a#back-to-top").fadeOut();
		} else {
			$("a#back-to-top").fadeIn();
		}

	}

	function buildStyleSheet(){
		var primaryColor60 = "rgba(" + hexToRgb('#182b52') + ", .60)";
		var primaryColor50 = "rgba(" + hexToRgb('#182b52') + ", .50)";
		var primaryColor40 = "rgba(" + hexToRgb('#182b52') + ", .40)";
		var primaryColor20 = "rgba(" + hexToRgb('#182b52') + ", .20)";

		var primaryColor = "#182b52";
		primaryColor = primaryColor.replace('#', '');

		var dynStyleSheet = document.createElement('style');

		if(dynStyleSheet) {
			dynStyleSheet.setAttribute('type', 'text/css');
			var head = document.getElementsByTagName('head')[0];

			if(head) {
				head.insertBefore(dynStyleSheet, head.childNodes[0]);
			}

			var dynStyles =	'#hp-slideshow .rs-gallery-container .rs-gallery-desc {' +
                    				'background: ' + primaryColor60 + ';' +
						'-pie-background: ' + primaryColor60 + ';' +
					'}' +
					/*'body #sw-mystart-outer {' +
                    				'background: ' + primaryColor40 + ';' +
						'-pie-background: ' + primaryColor40 + ';' + 
						'position: relative;' +
						'behavior: url(/cms/lib/MA01907692/Centricity/Template/8/scripts/PIE.htc);' +

					'}' +*/
					'.ui-widget.app.upcomingevents li {' +
                    				'border-bottom: 1px solid ' + primaryColor50 + ';' +

					'}' +
					'.ui-widget.app.headlines li {' +
                    				'border-bottom: 1px solid ' + primaryColor20 + ';' +

					'}' +
					'#hp-content:before {' +
                    				'border-left: 1px solid ' + primaryColor50 + ';' +
						'border-right: 1px solid ' + primaryColor50 + ';' +

					'}' +
					'.hp.column.three .hp-column-three-top {' +
                    				'border-bottom: 1px solid ' + primaryColor50 + ';' +

					'}' +
					'@media (max-width: 767px) {' +
						'#hp-content .column-inner .ui-widget.app {' +	
							'border-bottom: 1px solid ' + primaryColor50 +';' +
						'}' +	
					'}';

			var rules = document.createTextNode(dynStyles);

			if(dynStyleSheet.styleSheet){ // IE
				dynStyleSheet.styleSheet.cssText = dynStyles;
			} else {
				dynStyleSheet.appendChild(rules);
			}
		}
	}

	function hexToRgb(h){
	        var r = parseInt((cutHex(h)).substring(0,2),16);
	        var g = parseInt((cutHex(h)).substring(2,4),16);
	        var b = parseInt((cutHex(h)).substring(4,6),16);
	        return r + ', ' + g + ', ' + b;
	}

	function cutHex(h) {
	        return (h.charAt(0)=="#") ? h.substring(1,7):h;
	}

	function searchText() {
		//DOC custom mystart search placeholder
	        //SEARCH TEXT
	        $("div#sw-mystart-search input#sw-search-input").removeAttr("placeholder");
		$("div#sw-mystart-search input#sw-search-input").attr("value", "search");
		$("div#sw-mystart-search input#sw-search-input").focus(function() {
			if($(this).val() == "search") {
				$(this).val("");
			}
		});
		$("div#sw-mystart-search input#sw-search-input").blur(function() {
			if($(this).val() == "") {
				$(this).val("search");
			}
		});
	}

	//REMOVE LOGO ERROR IF NONE SELECTED
	function removeLogo(){
		$("#gb-logo").remove();
	}

	function eventsByDay(container) {
		//DOC move all events with the same dates into the same container
		var countLi = 0;
		$(".ui-article", container).each(function(){
			if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
				var moveArticle = $(this).html();
				$(this).parent().prev().children().children().next().append(moveArticle);
				$(this).parent().remove();

			};
		});
	}

	function calendarListView(){
		//DOC auto click function for schoolwires calendar list view for small mobile devices
		if(!$("a.ui-btn-toggle.list").hasClass("active")){
			$("a.ui-btn-toggle.list").click();
		}
	}

	function globalIcons(){
		        var icon1Target = 'Same Window';
		        var icon2Target = 'Same Window';
		        var icon3Target = 'Same Window';
		        var icon4Target = 'Same Window';
		        if(icon1Target == "New Window"){
		        	icon1Target = "_blank";
		        } else {
		        	icon1Target = "_self";
		        }
		        if(icon2Target == "New Window"){
		        	icon2Target = "_blank";
		        } else {
		        	icon2Target = "_self";
		        }
		        if(icon3Target == "New Window"){
		        	icon3Target = "_blank";
		        } else {
		        	icon3Target = "_self";
		        }
		        if(icon4Target == "New Window"){
		        	icon4Target = "_blank";
		        } else {
		        	icon4Target = "_self";
		        }
		        $(".icn.facebook").removeClass("hidden").attr("target", icon1Target);
		        $(".icn.facebook").removeClass("hidden").attr("target", icon2Target);
		        $(".icn.facebook").removeClass("hidden").attr("target", icon3Target);
		        $(".icn.facebook").removeClass("hidden").attr("target", icon4Target);
		}

	function socialIcons(){
		// SET UP THE CHECK VARIABLES
		var hasIcons = 0;
		var facebookCheck = 'true';
		var twitterCheck = 'true';
		var youtubeCheck = 'true';
		var linkedInCheck = 'true';
		var flickrCheck = 'false';
        var instagramCheck = 'true';
        var pinterestCheck = 'false';
        var googlePlusCheck = 'false';
        var vimeoCheck = 'false';
        var snapchatCheck = 'false';

		// SHOW THE ICONS THAT ARE NEEDED
	    	if(facebookCheck == "true"){
		        var facebookTarget = 'New Window';
		        if(facebookTarget == "New Window"){
		        	facebookTarget = "_blank";
		        } else {
		        	facebookTarget = "_self";
		        }
		        $(".icn.facebook").removeClass("hidden").attr("target", facebookTarget);
			hasIcons = 1;
		}
	    	if(twitterCheck == "true"){
		        var twitterTarget = 'New Window';
		        if(twitterTarget == "New Window"){
		        	twitterTarget = "_blank";
		        } else {
		        	twitterTarget = "_self";
		        }
		        $(".icn.twitter").removeClass("hidden").attr("target", twitterTarget);
		        hasIcons = 1;
		}
		if(linkedInCheck == "true"){
		        var linkedInTarget = 'New Window';
		        if(linkedInTarget == "New Window"){
		        	linkedInTarget = "_blank";
		        } else {
		        	linkedInTarget = "_self";
		        }
		        $(".icn.linkedIn").removeClass("hidden").attr("target", linkedInTarget);
		        hasIcons = 1;
		}
		if(youtubeCheck == "true"){
		        var youtubeTarget = 'New Window';
		        if(youtubeTarget == "New Window"){
		        	youtubeTarget = "_blank";
		        } else {
		        	youtubeTarget = "_self";
		        }
		        $(".icn.youtube").removeClass("hidden").attr("target", youtubeTarget);
		        hasIcons = 1;
		}
		if(flickrCheck == "true"){
		        var flickrTarget = 'Same Window';
		        if(flickrTarget == "New Window"){
		        	flickrTarget = "_blank";
		        } else {
		        	flickrTarget = "_self";
		        }
		        $(".icn.flickr").removeClass("hidden").attr("target", flickrTarget);
		        hasIcons = 1;
		}
        if(instagramCheck == "true"){
		        var instagramTarget = 'New Window';
		        if(instagramTarget == "New Window"){
		        	instagramTarget = "_blank";
		        } else {
		        	instagramTarget = "_self";
		        }
		        $(".icn.instagram").removeClass("hidden").attr("target", instagramTarget);
		        hasIcons = 1;
		}
        if(pinterestCheck == "true"){
		        var pinterestTarget = 'Same Window';
		        if(pinterestTarget == "New Window"){
		        	pinterestTarget = "_blank";
		        } else {
		        	pinterestTarget = "_self";
		        }
		        $(".icn.pinterest").removeClass("hidden").attr("target", pinterestTarget);
		        hasIcons = 1;
		}
        if(googlePlusCheck == "true"){
		        var googlePlusTarget = 'Same Window';
		        if(googlePlusTarget == "New Window"){
		        	googlePlusTarget = "_blank";
		        } else {
		        	googlePlusTarget = "_self";
		        }
		        $(".icn.googlePlus").removeClass("hidden").attr("target", googlePlusTarget);
		        hasIcons = 1;
		}
        if(vimeoCheck == "true"){
		        var vimeoTarget = 'Same Window';
		        if(vimeoTarget == "New Window"){
		        	vimeoTarget = "_blank";
		        } else {
		        	vimeoTarget = "_self";
		        }
		        $(".icn.vimeo").removeClass("hidden").attr("target", vimeoTarget);
		        hasIcons = 1;
		}
        if(snapchatCheck == "true"){
		        var snapchatTarget = 'Same Window';
		        if(snapchatTarget == "New Window"){
		        	snapchatTarget = "_blank";
		        } else {
		        	snapchatTarget = "_self";
		        }
		        $(".icn.snapchat").removeClass("hidden").attr("target", snapchatTarget);
		        hasIcons = 1;
		}

		// SHOW THE ICONS IF THERE ARE ANY
		if(hasIcons == 1){
			$("#gb-social-icons").removeClass("hidden");
			$(".gb.footer.two h2").removeClass("hidden");
		}
	}

	function tabCheck(){
		// SET UP THE CHECK VARIABLES
		var tabOneCheck = 'true';
		var tabTwoCheck = 'true';
		var tabThreeCheck = 'true';
		var tabFourCheck = 'true';

		// SHOW THE ICONS THAT ARE NEEDED
	    	if(tabOneCheck == "false"){
		        $("#gb-global-tabs .tab.one").addClass("hidden");
		}
		if(tabTwoCheck == "false"){
		        $("#gb-global-tabs .tab.two").addClass("hidden");
		}
		if(tabThreeCheck == "false"){
		        $("#gb-global-tabs .tab.three").addClass("hidden");
		}
		if(tabFourCheck == "false"){
		        $("#gb-global-tabs .tab.four").addClass("hidden");
		}

	}

