link.script:
        $(document).ready(function () {
            SetCookie('SWScreenWidth', screen.width);
            SetCookie('SWClientWidth', document.body.clientWidth);
            
            $("div.ui-article:last").addClass("last-article");
            $("div.region .app:last").addClass("last-app");

            // get on screen alerts
            var isAnyActiveOSA = 'False';
            var onscreenAlertCookie = GetCookie('Alerts');

            if (onscreenAlertCookie == '' || onscreenAlertCookie == undefined) {
                onscreenAlertCookie = "";
            }
            if (isAnyActiveOSA == 'True') {
                GetContent(homeURL + "/cms/Tools/OnScreenAlerts/UserControls/OnScreenAlertDialogListWrapper.aspx?OnScreenAlertCookie=" + onscreenAlertCookie + "&SiteID=4", "onscreenalert-holder", 2, "OnScreenAlertCheckListItem();");
            }

            

        });

    // ADA SKIP NAV
    $(document).ready(function () {
        $(document).on('focus', '#skipLink', function () {
            $("div.sw-skipnav-outerbar").animate({
                marginTop: "0px"
            }, 500);
        });

        $(document).on('blur', '#skipLink', function () {
            $("div.sw-skipnav-outerbar").animate({
                marginTop: "-30px"
            }, 500);
        });
    });

    // ADA MYSTART
    $(document).ready(function () {
        var top_level_nav = $('.sw-mystart-nav');

        // Set tabIndex to -1 so that top_level_links can't receive focus until menu is open
        // school dropdown
        $(top_level_nav).find('ul').find('a').attr('tabIndex', -1);

        // my account dropdown
        $(top_level_nav).next('ul').find('a').attr('tabIndex', -1);

        var openNavCallback = function(e, element) {
             // hide open menus
            hideMyStartBarMenu();

            // show school dropdown
            if ($(element).find('ul').length > 0) {
                $(element).find('.sw-dropdown').css('display', 'block').attr('aria-hidden', 'false');
                $(element).find('.sw-dropdown').find('li:first-child a').focus()
            }

            // show my account dropdown
            if ($(element).next('ul').length > 0) {
                $(element).next('.sw-dropdown').css('display', 'block').attr('aria-hidden', 'false');
                $(element).next('.sw-dropdown').find('li:first-child a').focus();
                $('#sw-mystart-account').addClass("clicked-state");
            }
        }

        $(top_level_nav).click(function (e) {
            openNavCallback(e, this);
        });

        $('.sw-dropdown-list li').click(function(e) {
            e.stopImmediatePropagation();
            $(this).focus();
        });
        
        // Bind arrow keys for navigation
        $(top_level_nav).keydown(function (e) {
            if (e.keyCode == 37) { //key left
                e.preventDefault();

                // This is the first item
                if ($(this).prev('.sw-mystart-nav').length == 0) {
                    $(this).parents('div').find('.sw-mystart-nav').last().focus();
                } else {
                    $(this).prev('.sw-mystart-nav').focus();
                }
            } else if (e.keyCode == 38) { //key up
                e.preventDefault();

                // show school dropdown
                if ($(this).find('ul').length > 0) {
                    $(this).find('div.sw-dropdown').css('display', 'block').find('ul').attr('aria-hidden', 'false').find('a').attr('tabIndex', 0).last().focus();
                }

                // show my account dropdown
                if ($(this).find('ul').length > 0) {
                    $(this).find('ul.sw-dropdown').css('display', 'block').attr('aria-hidden', 'false').find('a').attr('tabIndex', 0).last().focus();
                }
            } else if (e.keyCode == 39) { //key right
                e.preventDefault();

                // This is the last item
                if ($(this).next('.sw-mystart-nav').length == 0) {
                    $(this).parents('div').find('.sw-mystart-nav').first().focus();
                } else {
                    $(this).next('.sw-mystart-nav').focus();
                }
            } else if (e.keyCode == 40) { //key down
                e.preventDefault();

                // show school dropdown
                if ($(this).find('ul').length > 0) {
                    $(this).find('div.sw-dropdown').css('display', 'block').find('ul').attr('aria-hidden', 'false').find('a').attr('tabIndex', 0).first().focus();
                }

                // show my account dropdown
                if ($(this).next('ul').length > 0) {
                    $(this).next('ul.sw-dropdown').css('display', 'block').attr('aria-hidden', 'false').find('a').attr('tabIndex', 0).first().focus();
                }
            } else if (e.keyCode == 13 || e.keyCode == 32) { //enter key
                // If submenu is hidden, open it
                e.preventDefault();

                
                openNavCallback(e, this);
                $(this).parent('li').find('ul[aria-hidden=true]').attr('aria-hidden', 'false').find('a').attr('tabIndex', 0).first().focus();
            } else if (e.keyCode == 27) { //escape key
                e.preventDefault();
                hideMyStartBarMenu();
            } else {
                $(this).parent('.sw-mystart-nav').find('ul[aria-hidden=false] a').each(function () {
                    if (typeof keyCodeMap != "undefined" && $(this).text().substring(0, 1).toLowerCase() == keyCodeMap[e.keyCode]) {
                        $(this).focus();
                        return false;
                    }
                });
            }
        });

        // school dropdown
        var startbarlinks = $(top_level_nav).find('ul').find('a');
        bindMyStartBarLinks(startbarlinks);

        // my account dropdown
        var myaccountlinks = $(top_level_nav).next('ul').find('a');
        bindMyStartBarLinks(myaccountlinks);

        function bindMyStartBarLinks(links) {
            $(links).keydown(function (e) {
                e.stopPropagation();

                if (e.keyCode == 38) { //key up
                    e.preventDefault();

                    // This is the first item
                    if ($(this).parent('li').prev('li').length == 0) {
                        if ($(this).parents('ul').parents('.sw-mystart-nav').length > 0) {
                            $(this).parents('ul').parents('.sw-mystart-nav').focus();
                        } else {
                            $(this).parents('ul').prev('.sw-mystart-nav').focus();
                        }
                    } else {
                        $(this).parent('li').prev('li').find('a').first().focus();
                    }
                } else if (e.keyCode == 40) { //key down
                    e.preventDefault();

                    if ($(this).parent('li').next('li').length == 0) {
                        if ($(this).parents('ul').parents('.sw-mystart-nav').length > 0) {
                            $(this).parents('ul').parents('.sw-mystart-nav').focus();
                        } else {
                            $(this).parents('ul').prev('.sw-mystart-nav').focus();
                        }
                    } else {
                        $(this).parent('li').next('li').find('a').first().attr('tabIndex', 0);
                        $(this).parent('li').next('li').find('a').first().focus();
                    }
                } else if (e.keyCode == 27 || e.keyCode == 37) { // escape key or key left
                    e.preventDefault();
                    hideMyStartBarMenu();
                } else if (e.keyCode == 32) { //enter key
                    e.preventDefault();
                    window.location = $(this).attr('href');
                } else {
                    var found = false;

                    $(this).parent('div').nextAll('li').find('a').each(function () {
                        if (typeof keyCodeMap != "undefined" && $(this).text().substring(0, 1).toLowerCase() == keyCodeMap[e.keyCode]) {
                            $(this).focus();
                            found = true;
                            return false;
                        }
                    });

                    if (!found) {
                        $(this).parent('div').prevAll('li').find('a').each(function () {
                            if (typeof keyCodeMap != "undefined" && $(this).text().substring(0, 1).toLowerCase() == keyCodeMap[e.keyCode]) {
                                $(this).focus();
                                return false;
                            }
                        });
                    }
                }
            });
        }
        
        // Hide menu if click or focus occurs outside of navigation
        $('#sw-mystart-inner').find('.sw-mystart-nav').last().keydown(function (e) {
            if (e.keyCode == 9) {
                // If the user tabs out of the navigation hide all menus
                hideMyStartBarMenu();
            }
        });

        /*$(document).click(function() { 
            hideMyStartBarMenu();
        });*/

        // try to capture as many custom MyStart bars as possible
        $('.sw-mystart-button').find('a').focus(function () {
            hideMyStartBarMenu();
        });

        $('#sw-mystart-inner').click(function (e) {
            e.stopPropagation();
        });

        $('ul.sw-dropdown-list').blur(function () {
            hideMyStartBarMenu();
        });

        $('#ui-btn-mypasskey').focus(function () {
            hideMyStartBarMenu();
        });

        $('#ui-btn-sitemanager').focus(function () {
            hideMyStartBarMenu();
        });

        $('#ui-btn-myview').focus(function () {
            hideMyStartBarMenu();
        });

        $('#ui-btn-signin').focus(function () {
            hideMyStartBarMenu();
        });

        $('#ui-btn-register').focus(function () {
            hideMyStartBarMenu();
        });

        // button click events
        $('div.sw-mystart-button.home a').keydown(function (e) {
            e.stopImmediatePropagation();

            if (e.keyCode == 13) {
                $(this).click();
            }
        });

        $('div.sw-mystart-button.pw a').keydown(function (e) {
            e.stopImmediatePropagation();

            if (e.keyCode == 13) {
                $(this).click();
            }
        });

        $('div.sw-mystart-button.manage a').keydown(function (e) {
            e.stopImmediatePropagation();

            if (e.keyCode == 13) {
                $(this).click();
            }
        });

        $('#sw-mystart-account').keydown(function (e) {
            e.stopImmediatePropagation();

            if (e.keyCode == 13) {
                $(this).addClass('clicked-state');
                $('#sw-myaccount-list').show();
            }
        });

        $('#sw-mystart-mypasskey a').keydown(function (e) {
            e.stopImmediatePropagation();

            if (e.keyCode == 13) {
                $(this).click();
            }
        });

        $('div.sw-mystart-button.signin a').keydown(function (e) {
            e.stopImmediatePropagation();

            if (e.keyCode == 13) {
                $(this).click();
            }
        });

        $('div.sw-mystart-button.register a').keydown(function (e) {
            e.stopImmediatePropagation();

            if (e.keyCode == 13) {
                $(this).click();
            }
        });
    });

    function hideMyStartBarMenu() {
        $('.sw-dropdown').attr('aria-hidden', 'true').css('display', 'none');
        $('#sw-mystart-account').removeClass("clicked-state");
    }

    // ADA CHANNEL NAV
    $(document).ready(function() {
        var channelCount;
        var channelIndex = 1;
        var settings = {
            menuHoverClass: 'hover'
        };

        // Add ARIA roles to menubar and menu items
        $('[id="channel-navigation"]').attr('role', 'menubar').find('li a').attr('role', 'menuitem').attr('tabindex', '0');

        var top_level_links = $('[id="channel-navigation"]').find('> li > a');
        channelCount = $(top_level_links).length;


        $(top_level_links).each(function() {
            $(this).attr('aria-posinset', channelIndex).attr('aria-setsize', channelCount);
            $(this).next('ul').attr({ 'aria-hidden': 'true', 'role': 'menu' });

            if ($(this).parent('li.sw-channel-item').children('ul').length > 0) {
                $(this).attr('aria-haspopup', 'true');
            }

            var sectionCount = $(this).next('ul').find('a').length;
            var sectionIndex = 1;
            $(this).next('ul').find('a').each(function() {
                $(this).attr('tabIndex', -1).attr('aria-posinset', sectionIndex).attr('aria-setsize', sectionCount);
                sectionIndex++;
            });
            channelIndex++;

        });

        $(top_level_links).focus(function () {
            //hide open menus
            hideChannelMenu();

            if ($(this).parent('li').find('ul').length > 0) {
                $(this).parent('li').addClass(settings.menuHoverClass).find('ul').attr('aria-hidden', 'false').css('display', 'block');
            }
        });

        // Bind arrow keys for navigation
        $(top_level_links).keydown(function (e) {
            if (e.keyCode == 37) { //key left
                e.preventDefault();

                // This is the first item
                if ($(this).parent('li').prev('li').length == 0) {
                    $(this).parents('ul').find('> li').last().find('a').first().focus();
                } else {
                    $(this).parent('li').prev('li').find('a').first().focus();
                }
            } else if (e.keyCode == 38) { //key up
                e.preventDefault();

                if ($(this).parent('li').find('ul').length > 0) {
                    $(this).parent('li').addClass(settings.menuHoverClass).find('ul').css('display', 'block').attr('aria-hidden', 'false').find('a').attr('tabIndex', 0).last().focus();
                }
            } else if (e.keyCode == 39) { //key right
                e.preventDefault();

                // This is the last item
                if ($(this).parent('li').next('li').length == 0) {
                    $(this).parents('ul').find('> li').first().find('a').first().focus();
                } else {
                    $(this).parent('li').next('li').find('a').first().focus();
                }
            } else if (e.keyCode == 40) { //key down
                e.preventDefault();

                if ($(this).parent('li').find('ul').length > 0) {
                    $(this).parent('li')
                         .addClass(settings.menuHoverClass)
                         .find('ul.sw-channel-dropdown').css('display', 'block')
                         .attr('aria-hidden', 'false')
                         .find('a').attr('tabIndex', 0)
                         .first().focus();
                }
            } else if (e.keyCode == 13 || e.keyCode == 32) { //enter key
                // If submenu is hidden, open it
                e.preventDefault();

                $(this).parent('li').find('ul[aria-hidden=true]').attr('aria-hidden', 'false').addClass(settings.menuHoverClass).find('a').attr('tabIndex', 0).first().focus();
            } else if (e.keyCode == 27) { //escape key
                e.preventDefault();
                hideChannelMenu();
            } else {
                $(this).parent('li').find('ul[aria-hidden=false] a').each(function () {
                    if (typeof keyCodeMap != "undefined" && $(this).text().substring(0, 1).toLowerCase() == keyCodeMap[e.keyCode]) {
                        $(this).focus();
                        return false;
                    }
                });
            }
        });

        var links = $(top_level_links).parent('li').find('ul').find('a');

        $(links).keydown(function (e) {
            if (e.keyCode == 38) {
                e.preventDefault();

                // This is the first item
                if ($(this).parent('li').prev('li').length == 0) {
                    $(this).parents('ul').parents('li').find('a').first().focus();
                } else {
                    $(this).parent('li').prev('li').find('a').first().focus();
                }
            } else if (e.keyCode == 40) {
                e.preventDefault();

                if ($(this).parent('li').next('li').length == 0) {
                    $(this).parents('ul').parents('li').find('a').first().focus();
                } else {
                    $(this).parent('li').next('li').find('a').first().focus();
                }
            } else if (e.keyCode == 27 || e.keyCode == 37) {
                e.preventDefault();
                $(this).parents('ul').first().prev('a').focus().parents('ul').first().find('.' + settings.menuHoverClass).removeClass(settings.menuHoverClass);
            } else if (e.keyCode == 32) {
                e.preventDefault();
                window.location = $(this).attr('href');
            } else {
                var found = false;

                $(this).parent('li').nextAll('li').find('a').each(function () {
                    if (typeof keyCodeMap != "undefined" && $(this).text().substring(0, 1).toLowerCase() == keyCodeMap[e.keyCode]) {
                        $(this).focus();
                        found = true;
                        return false;
                    }
                });

                if (!found) {
                    $(this).parent('li').prevAll('li').find('a').each(function () {
                        if (typeof keyCodeMap != "undefined" && $(this).text().substring(0, 1).toLowerCase() == keyCodeMap[e.keyCode]) {
                            $(this).focus();
                            return false;
                        }
                    });
                }
            }
        });

        function hideChannelMenu() {
            $('li.sw-channel-item.' + settings.menuHoverClass).removeClass(settings.menuHoverClass).find('ul').attr('aria-hidden', 'true').css('display', 'none').find('a').attr('tabIndex', -1);
        }
        
        // Hide menu if click or focus occurs outside of navigation
        $('[id="channel-navigation"]').find('a').last().keydown(function (e) {
            if (e.keyCode == 9) {
                // If the user tabs out of the navigation hide all menus
                hideChannelMenu();
            }
        });

        $('[id="channel-navigation"]').find('a').first().keydown(function (e) {
            if (e.keyCode == 9) {
                // hide open MyStart Bar menus
                hideMyStartBarMenu();
            }
        });

        /*$(document).click(function() {
            hideChannelMenu();
        });*/

        $('[id="channel-navigation"]').click(function (e) {
            e.stopPropagation();
        });
    });

    $(document).ready(function() {
        $('input.required').each(function() {
            if ($('label[for="' + $(this).attr('id') + '"]').length > 0) {
                if ($('label[for="' + $(this).attr('id') + '"]').html().indexOf('recStar') < 0) {
                    $('label[for="' + $(this).attr('id') + '"]').prepend('* ');
                }
            }
        });

        $(document).ajaxComplete(function() {
            $('input.required').each(function() {
                if ($('label[for="' + $(this).attr('id') + '"]').length > 0) {
                    if ($('label[for="' + $(this).attr('id') + '"]').html().indexOf('recStar') < 0) {
                        $('label[for="' + $(this).attr('id') + '"]').prepend('* ');
                    }
                }
            });
        });
    });

	$(document).ready(function(){		
		schoolLevel();
	});
	function initialize(){
		channelBarHeight();
		customLogo();
		dynamicContent();
		loadFlash();
		noFlash();
		sameHeight();
		// FIRST VARIABLE IS TIMING, SECOND IS FADE SPEED
		// PHOTO GALLERY CONFIGURATION
		//DOC #sw-content-container10 -- INITIALIZE PHOTO GALLERY
		$(".ui-hp#sw-content-container10.region.ui-hp div.ui-widget.app.file-library").joelRotate({
			"delay": 8,
			"fadeSpeed": 2,
			"useTitles": "no",
			"useDescriptions": "no",
			"useLinks": "no"
		});
	}
	function channelBarHeight(){
		// SETS ADJUSTMENTS FOR MULTI-LINE CHANNEL BAR
		var channelHeight = $("#sw-channel-list-container").height();
		$("ul#channel-navigation").height(channelHeight);
		$("#sw-channel-list-container").css("overflow","visible");
		$("#gb-header").css("top",channelHeight + "px");

		var absoluteHeightAdjustment = channelHeight - 39;
		var slideshowTop = parseInt($("#hp-slideshow").css("top")) + absoluteHeightAdjustment;
		var curveTop = parseInt($("#hp-lower-curve").css("top")) + absoluteHeightAdjustment;

		$("div#hp-slideshow").css("top",slideshowTop);
		$("div#hp-lower-curve").css("top",curveTop);
	}
	function schoolLevel(){
		// IF schoolTextFiles IS ON THEN ADD THE DYNAMIC TEXT FILE
		if(schoolTextFiles == "on")
		{
			$.ajax({
			  url: "/cms/lib/IL01001779/Centricity/Template/8/setup/whsd1.txt",
			  dataType: "script",
			  success: function() {
			    initialize();
			  },
			  error: function()
			  {
			  	alert("An error has occured initializing setup files.");
			  }
			});
		}
		else
		{
			initialize();
		}

	}
	function customLogo(){
		if(customLogos != "none") {
			// #gb-logo a img -- IF customLogos IS ON THEN ADD THE customLogo
			if(customLogos == "on") {
				$("#gb-logo a img").attr("src","/cms/lib/IL01001779/Centricity/Template/8/logos/whsd1.png");
			} else {
				$("#gb-logo a img").attr("src","/cms/lib/IL01001779/Centricity/Template/8/logos/_logo.png");
			}
			$("#gb-logo").show(); 
		}
	}
	function noFlash() {
		// #mymovie -- SEE IF #mymovie.swf EXISTS IF NOT RUN THE CODE BELOW AS A FALL BACK
		if($("#mymovie").size() == 0) {
			$("#gb-header, #gb-flash-header").height(100);
			$("#gb-flash-header").css("background-color",primaryColor);
			$("#gb-header").css("border-bottom","3px solid" + secondaryColor);
			$(".sp.column.one, .sp.column.two").css("margin-top","0px");
			$("#sp-content-container, #nn-content-container").css({"margin-top":"103px","padding-top":"0px"});
			$(".hp.column.one,.hp.column.three").css("margin-top","103px");
			$(".hp.column.two").css("margin-top","354px");
			$("#hp-slideshow").css("top","142px");
		}
	}
	function dynamicContent() {
		//DOC .sw-mystart-button.home -- SETS DISTRICT BUTTON ON/OFF
		if(districtButton == "off"){
			$(".sw-mystart-button.home").hide();

		}
		// SETS BODY COLOR
		$("body").css("background-color",backgroundColor);

		// SETS CHANNEL BAR STYLES
		$("#sw-channel-list-container, #gb-footer").css("background-color",secondaryColor);
		$("#sw-channel-list-container .sw-channel-item > a").css("color",channelUnselectedColor);
		$("#sw-channel-list-container li.sw-channel-item.active > a").css("color",channelSelectedColor);
		$("li.sw-channel-item > a").hover(function(){
	        	$(this).css("color", channelSelectedColor);
	        }, function(){
	       		$(this).css("color",channelUnselectedColor);
	       		$("#sw-channel-list-container li.sw-channel-item.active > a").css("color",channelSelectedColor);
	        });

		// SETS COLUMN COLORS
		$(".hp.column.one, .sp.column.one").css("background-color",primaryColor);
		$(".hp.column.three").css("background-color",accentColor);
		$("ul.ui-breadcrumbs li > a").css("color",primaryColor);

		//DOC #sw-content-container1 .ui-widget-header h1 -- SETS REGION BODY AND HEADER COLORS
		$(".ui-hp#sw-content-container1 .ui-widget-header h1").css({"color": regionOneHeaderColor, "border-color": regionOneHeaderColor});
		$(".ui-hp#sw-content-container1").css("color",regionOneBodyColor);
		$(".ui-hp#sw-content-container1 a, .ui-hp#sw-content-container1 > a > span").css("color",regionOneLinkColor);
		//DOC #sw-content-container2 .ui-widget-header h1 -- SETS REGION BODY AND HEADER COLORS
		$(".ui-hp#sw-content-container2 .ui-widget-header h1").css({"color": regionTwoHeaderColor, "border-color": regionTwoHeaderColor});
		$(".ui-hp#sw-content-container2").css("color",regionTwoBodyColor);
		$(".ui-hp#sw-content-container2 a, .ui-hp#sw-content-container1 > a > span").css("color",regionTwoLinkColor);
		//DOC #sw-content-container3 .ui-widget-header h1 -- SETS REGION BODY AND HEADER COLORS
		$(".ui-hp#sw-content-container3 .ui-widget-header h1").css({"color": regionThreeHeaderColor, "border-color": regionThreeHeaderColor});
		$(".ui-hp#sw-content-container3").css("color",regionThreeBodyColor);
		$(".ui-hp#sw-content-container3 a, .ui-hp#sw-content-container1 > a > span").css("color",regionThreeLinkColor);
		//DOC #sw-content-container4 .ui-widget-header h1 -- SETS REGION BODY AND HEADER COLORS
		$(".ui-hp#sw-content-container4 .ui-widget-header h1").css({"color": regionFourHeaderColor, "border-color": regionFourHeaderColor});
		$(".ui-hp#sw-content-container4").css("color",regionFourBodyColor);
		$(".ui-hp#sw-content-container4 a, .ui-hp#sw-content-container1 > a > span").css("color",regionFourLinkColor);
		//DOC #sw-content-container5 .ui-widget-header h1 -- SETS REGION BODY AND HEADER COLORS
		$(".ui-hp#sw-content-container5 .ui-widget-header h1").css({"color": regionFiveHeaderColor, "border-color": regionFiveHeaderColor});
		$(".ui-hp#sw-content-container5").css("color",regionFiveBodyColor);
		$(".ui-hp#sw-content-container5 a, .ui-hp#sw-content-container1 > a > span").css("color",regionFiveLinkColor);
		$("div#nn-content-container div.region, div#nn-content-container div.region a, div#nn-content-container div.region span").css("color","#000");


		// SETS HEADER COLORS AND TEXT

		if(schoolNameText == "default")
		{
			schoolNameText = "Winthrop Harbor School District 1";
		}

		// #gb-header h1#gb-name a -- SETS SCHOOL NAME
		$("#gb-header h1#gb-name a").html(schoolNameText);
		// #gb-header h1#gb-name a -- SETS SCHOOL NAME COLOR
		$("#gb-header h1#gb-name a").css("color",schoolNameColor);

		// #gb-header #gb-tagline a -- SETS TAGLINE
		$("#gb-header #gb-tagline a").html(taglineText);
		// #gb-header #gb-tagline a -- SETS TAGLINE COLOR
		$("#gb-header #gb-tagline a").css("color",taglineColor);


		// #gb-footer, #gb-footer a -- SETS FOOTER TEXT COLOR
		//sets footer text color
		$("#gb-footer, #gb-footer a").css("color",footerTextColor);


		$(".sp.column.one a").css("color",regionOneBodyColor);
		$(".sp.column.one .ui-widget-header h1").css("color",regionOneHeaderColor);
		$("div.ui-widget.app.pagenavigation ul.sw-pagenavigation-control li.sw-pagenavigation-control-link.active > a").css("color",subpageSelectedColor);
	}
	function sameHeight(){

		// #mymovie -- SETS HEIGHT FOR PAGE WITH FLASH ENABLES
		if($("#mymovie").size())
		{
			var contentHeight = $(".column").parent().height();
			if(contentHeight < 350)
			{
				contentHeight = 350;
			}
			$(".hp.column.one").height(contentHeight-159);
		      	$(".hp.column.two").height(contentHeight-305);
			$(".hp.column.three").height(contentHeight-135);

			// IF .children() OF SELECTOR RESIZES THEN RESET HEIGHT OF SELECTOR
			$(".hp.column").children().resize(function() {
				$(".hp.column").height("");
				contentHeight = $(".column").parent().height();
				$(".hp.column.one").height(contentHeight-159);
				$(".hp.column.two").height(contentHeight-305);
				$(".hp.column.three").height(contentHeight-135);	
			});

			spMaxHeight = $("#sp-content").height();
			$(".sp.column.one, .sp.column.two").height(spMaxHeight);
			$(".sp.column.one, .sp.column.two").children().resize(function()
			{	
				$(".sp.column.one, .sp.column.two").height("");
				spMaxHeight = $("#sp-content").height();
				$(".sp.column.one, .sp.column.two").height(spMaxHeight);
			});
       	}
       		// #mymovie -- SETS HEIGHT FOR PAGE WITH FLASH ENABLES
		else if($("#mymovie").size() == 0)
		{
			var contentHeight = $(".column").parent().height();
			$(".hp.column.one, .hp.column.three").height(contentHeight-103);
			$(".hp.column.two").height(contentHeight-354);	

			// IF .children() OF SELECTOR RESIZES THEN RESET HEIGHT OF SELECTOR
			$(".hp.column").children().resize(function() {
				$(".hp.column").height("");
				contentHeight = $(".column").parent().height();
				$(".hp.column.one, .hp.column.three").height(contentHeight-103);
				$(".hp.column.two").height(contentHeight-354);		
			});

			$(".sp.column.one, .sp.column.two").height(contentHeight);
				$(".sp.column").children().resize(function() {
				$(".sp.column").height("");
				contentHeight = $(".column").parent().height();
				$(".sp.column.one, .sp.column.two").height(contentHeight);
			});
		}
	}
	function loadFlash(){
		// #hp-flash-header -- LOADS MAIN FLASH HEADER
		// #hp-flash-header -- IF #hp-flash-header EXISTS THEN ADD THE SWFObject
		if($("#hp-flash-header").size())
	         {
	            var so = new SWFObject("/cms/lib/IL01001779/Centricity/Template/8/hp-flash-header.swf","mymovie", "960", "160", "9", "#FFFFFF");
	            so.addParam("wmode", "transparent");
	            so.addVariable("schoolAbbr", "whsd1");
	            so.addVariable("SiteID", "4");
	            so.addVariable("primaryColor",primaryColor);
	            so.addVariable("secondaryColor",secondaryColor);
	            if($(".hp").size())
	            {
	            	so.addVariable("accentColor",accentColor);
	            }
	            else
	            {
	            	so.addVariable("accentColor","#FFFFFF");
	            }
	            if($("#nn-content-container").size())
	            {
	            	so.addVariable("leftNav","off");
	            }
	            else
	            {
	            	so.addVariable("leftNav","on");
	            }
	            so.addVariable("homeURL", window.location.host);
	            so.write("hp-flash-header");
	          }
	          // #sp-flash-header -- IF #sp-flash-header EXISTS THEN ADD THE SWFObject
	          if($("#sp-flash-header").size())
	         {
	            var so = new SWFObject("/cms/lib/IL01001779/Centricity/Template/8/sp-flash-header.swf","mymovie", "960", "160", "9", "#FFFFFF");
	            so.addParam("wmode", "transparent");
	            so.addVariable("schoolAbbr", "whsd1");
	            so.addVariable("SiteID", "4");
	            so.addVariable("primaryColor",primaryColor);
	            so.addVariable("secondaryColor",secondaryColor);
	            if($(".hp").size())
	            {
	            	so.addVariable("accentColor",accentColor);
	            }
	            else
	            {
	            	so.addVariable("accentColor","#FFFFFF");
	            }
	            if($("#nn-content-container").size())
	            {
	            	so.addVariable("leftNav","off");
	            }
	            else
	            {
	            	so.addVariable("leftNav","on");
	            }
	            so.addVariable("homeURL", window.location.host);
	            so.write("sp-flash-header");
	          }

		// #hp-lower-curve -- LOADS LOWER CURVE
		// #hp-lower-curve -- IF #hp-lower-curve EXISTS THEN ADD THE SWFObject
	        if($("#hp-lower-curve").size())
	        {
	        var so = new SWFObject("/cms/lib/IL01001779/Centricity/Template/8/lowerCurve.swf","mymovie", "480", "52", "9", "#FFFFFF");
	            so.addParam("wmode", "transparent");
	            so.addVariable("schoolAbbr", "whsd1");
	            so.addVariable("SiteID", "4");
	            so.addVariable("homeURL", window.location.host);
	            so.addVariable("secondaryColor",secondaryColor);
	            so.write("hp-lower-curve");
	        }
        }

