                            var height = parseInt($('.sticky-hs').outerHeight());
                            if(isNaN(height) || height === null){
                                height = parseInt($('.for-urgentnotice').outerHeight());;
                            }else{
                                height = height;
                            }
                            if (this.hash !== '') {
                                event.preventDefault();
                                var hash = this.hash;
                                $('html, body').animate({
                                  scrollTop: $(hash).offset().top - height
                                }, 500, function(){
                                  window.location.hash = hash;
                                });
                                console.log(height);
                            }

                        });
                    });

