        "Search": function() {
        	var defSearchText = "Search...";

            var searchBox = $('#swsitesearch-txt-searchstring');

            searchBox
            	.attr('tabindex', -1)
            	.attr('aria-hidden', 'true')
                .attr("aria-label", "Search input")
                .attr("placeholder", '')
                .val(defSearchText)
                .focus(function() {
                    if($(this).val() == defSearchText) {
                        $(this).val("");
                    }
                })
                .blur(function() {
                    if($(this).val() == "") {
                        $(this).val(defSearchText);
                    }
                });

        	$('#search-trigger')
            .attr('aria-expanded', 'false')
            .off('click').click(function(e){
            	if( $('#swsitesearch-txt-searchstring').attr('aria-hidden') == "true" ){
                	$(this)
                    	.attr('aria-label', 'Close search container')
                        .attr('aria-expanded', 'true');

                	$('#swsitesearch-txt-searchstring')
                    	.attr('tabindex', 0)
                        .attr('aria-hidden', 'false');

                    if( $('.ios').length <= 0 ){
                        setTimeout(function(){
                            $('#swsitesearch-txt-searchstring').focus();
                        }, 300);
                    }

                    $('#gb-channel-list').addClass('hide-channels');
                }else{
                	$(this)
                    	.attr('aria-label', 'Open search container')
                    	.attr('aria-expanded', 'false');
                	$('#swsitesearch-txt-searchstring')
                    	.attr('tabindex', -1)
                        .attr('aria-hidden', 'true');
                    $('#gb-channel-list').removeClass('hide-channels');
                }
            });
        },
        "SmoothScroll": function(to, offset){
        	/*
            ** smoothScroll
            ** Use: This will smoothly scroll to the id that was passed to the function.
            ** Return: N/A
            ** Parameter(s): to (jQuery "#id") The id of the section to be scolled to.
            ** 		offset (int) The amount of space you want to be left on the top of the element after scroll (in a whole number int value)
            */
        	if( to.indexOf("#") < 0 ){ to = "#" + to; } //makes sure we have a valid id accessor string
            var target = $(to);

            if (target.length) {
                $('html,body').animate({
                    scrollTop: target.offset().top - offset //this offset accounts for the fixed menu on the top
                }, 800, function(){
                    $("html, body").stop(true, false);
                });
                return false;
            }
        },
        "JsDrawing": function(area){
        	switch( area ){
            	case "header":
                	$('.logo-triangle').each(function(canvasIndex, canvas){
                        if (canvas.getContext) {
                            var ctx = canvas.getContext('2d');

                            ctx.shadowColor = '#231f20';
							ctx.shadowBlur = 10;
                            ctx.shadowOffsetY = 2;
                            ctx.beginPath();
                            ctx.moveTo(0, 0);
                            ctx.lineTo(0, 32);
                            ctx.lineTo(202, 0);
                            ctx.fillStyle = "#FFF";
                            ctx.fill();
                        }
                    });

                    $('.logo-banner').each(function(canvasIndex, canvas){
                        if (canvas.getContext) {
                            var ctx = canvas.getContext('2d');

                            ctx.fillStyle = "#88171d";

                            ctx.beginPath();
                            ctx.moveTo(0, 0);
                            ctx.lineTo(0, 32);
                            ctx.lineTo(202, 0);
                            ctx.fill();
                            ctx.closePath();

                            ctx.beginPath();
                            ctx.moveTo(202, 0);
                            ctx.lineTo(202, 26);
                            ctx.lineTo(40, 0);
                            ctx.fill();
                            ctx.closePath();
                        }
                    });

                    var headerTriangle = document.getElementById('header-triangle');
                    if (headerTriangle != null && headerTriangle.getContext) {
                        var ctx = headerTriangle.getContext('2d');

                        ctx.fillStyle = "#145a35";

                        ctx.beginPath();
                        ctx.moveTo(0, 0);
                        ctx.lineTo(0, 233);
                        ctx.lineTo(1500, 0);
                        ctx.fill();
                        ctx.closePath();
                    }
                	break;

                case "footer":
                	var footerTriangle = document.getElementById('footer-triangle');
                    if (footerTriangle.getContext) {
                        var ctx = footerTriangle.getContext('2d');

                        ctx.fillStyle = "rgba(0, 0, 0, 0.17)";

                        ctx.beginPath();
                        ctx.moveTo(0, 0);
                        ctx.lineTo(0, 237);
                        ctx.lineTo(1500, 0);
                        ctx.fill();
                        ctx.closePath();
                    }
                	break;

                case "gallery":
                	var galleryTriangle = document.getElementById('gallery-triangle');
                    if (galleryTriangle != null && galleryTriangle.getContext) {
                        var ctx = galleryTriangle.getContext('2d');

                        ctx.fillStyle = "rgba(255, 255, 255, 0.37)";
                        ctx.beginPath();
                        ctx.moveTo(960, 0);
                        ctx.lineTo(960, 152);
                        ctx.lineTo(0, 152);
                        ctx.fill();
                        ctx.closePath();
                    }
                	break;
                default: //pass
                	break;
            }
        },


