        "AppScroller": function(){
        	if($(".hp").length) {

            	this.HeadlineCards();
            	var pass = 1;

                $('#headlines-scroller .ui-articles').addClass('old-list').after('');
            	$('#headlines-scroller .ui-articles > li').each(function(liIndex, li){
                    if(pass){
                    	$('#headlines-scroller .ui-articles.new-list').append('
' + $(li).html() + '');
                        pass = 0;
                    }else{
                    	$('#headlines-scroller .ui-articles.new-list li').last().append($(li).html());
                    	pass = 1;
                    }
				});

                $('#headlines-scroller .ui-articles.old-list').remove();

                $("#headlines-scroller .region .app.headlines").appScroller({
                        "appParent"             : "#headlines-scroller",
                        "targetApp"             : "headlines", //upcomingevents or headlines
                        "imageNum960"           : 3, //NUMBER OF IMAGES TO DISPLAY AT 960
                        "imageNum768"           : 2, //NUMBER OF IMAGES TO DISPLAY AT 768
                        "imageNum640"           : 1, //NUMBER OF IMAGES TO DISPLAY AT 640
                        "imageNum480"           : 1, //NUMBER OF IMAGES TO DISPLAY AT 480
                        "imageNum320"           : 1, //NUMBER OF IMAGES TO DISPLAY AT 320
                        "backIconFontLetter"    : "", //LETTER OR NUMBER TO USE FOR THE ICON FONT FOR THE BACK BUTTON
                        "nextIconFontLetter"    : "", //LETTER OR NUMBER TO USE FOR THE ICON FONT FOR THE BACK BUTTON
                        "buttonColor"           : "#FFF", //COLOR TO BE USED FOR THE BACK AND NEXT BUTTONS
                        "extraBackIconClass"    : "dev-icon-chevron-thin rotating-icon point-left",
                        "extraNextIconClass"    : "dev-icon-chevron-thin",
                        "controlGutter"         : "38" //LEFT and RIGHT MARGINS TO ALLOW ROOM FOR CONTROLS
                });

                $('#headlines-scroller .headlines .ui-articles > li .ui-article')
                	.attr('role', 'button')
                    .attr('tabindex', -1)
                    .attr('aria-label', 'Toggle this headline card');

                $('#headlines-scroller .app').each(function(appIndex, app){
                    if( $('.headlines-scroll-ctrl.back', app).length > 0 ){
                        $('.ui-widget-detail', app).before($('.headlines-scroll-ctrl.back', app));
                    }
                });

                $('#headlines-scroller .headlines .ui-articles > li.active .ui-article').attr('tabindex', 0);

                $('#headlines-scroller .headlines .ui-articles > li .ui-article').on('keydown', function(e) { // Trigger the click event from the keyboard
                    var code = e.which;
                    // 13 = Return, 32 = Space
                    if ((code === 13) || (code === 32)) {
                    	if(code === 32) e.preventDefault();
                        CreativeTemplate.ToggleHeadlineCards($(this));
                    }
                })
                .click(function(e){
                    CreativeTemplate.ToggleHeadlineCards($(this));
                })
                .hover(function(e){ //in
                        CreativeTemplate.ToggleHeadlineCards($(this));
                    },
                    function(e){ //out
                        CreativeTemplate.ToggleHeadlineCards($(this));
                });

                $('#headlines-scroller .headlines-scroll-ctrl').click(function(e){
                	$('#headlines-scroller .headlines .ui-articles > li .ui-article').attr('tabindex', -1);

                	$('#headlines-scroller .headlines .ui-articles > li.active .ui-article').attr('tabindex', 0);
                });
            }
        },


// APP METHODS
        "CalendarListView": function() {
            if($(".ui-widget.app.calendar").length) {
                // AUTO CLICK FUNCTION FOR SCHOOLWIRES CALENDAR LIST VIEW FOR SMALL MOBILE DEVICES
                if(!$(".ui-widget.app.calendar #calendarlist-pnl-specialview.loaded").length){
                    $("a.ui-btn-toggle.list").click();
                    $("a.ui-btn-toggle.month").click();
                    $(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("loaded");
                }

                switch(this.GetBreakPoint()) {
                    case "desktop": 
                    case "768": 
                    case "640":
                        $(".ui-widget.app.calendar #calendar").removeClass("ui-helper-hidden");
                        $(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("ui-helper-hidden");
                    break;
                    case "480": 
                    case "320":
                        $(".ui-widget.app.calendar #calendar").addClass("ui-helper-hidden");
                        $(".ui-widget.app.calendar #calendarlist-pnl-specialview").removeClass("ui-helper-hidden");
                        $(".ui-btn-toggle.month").removeClass("active");
                        $(".ui-btn-toggle.list").addClass("active");
                    break;
                }
            }
        },
        "UpcomingEvents": function(){
        	if (!$(".upcomingevents .joel-month").length) {
                $(".upcomingevents").modEvents({
                    columns     : "yes",
                    monthLong   : "yes",
                    dayWeek     : "yes"
                });

                $(".upcomingevents .ui-articles .ui-article").each(function(){
                    if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
                        var moveArticle = $(this).html();
                        $(this).parent().prev().children().children().next().append(moveArticle);
                        $(this).parent().remove();
                    };
                });

                $(".upcomingevents .ui-articles .ui-article").each(function(){
                    var newDateTime = $('.upcoming-column.left h1', this).html().toLowerCase().split("");
                    var newDate = '';
                    var dateIndex = ((newDateTime.length > 2) ? 2 : 1); //if the dayWeek is set to yes we need to account for that in our array indexing

                    //if we have the day of the week make sure to include it.
                    if( dateIndex > 1 ){ 
                        newDate += newDateTime[0] + "";
                    }

                    //add in the month
                    newDate += "
" + newDateTime[dateIndex - 1] + ''; //the month is always the in the left array position to the date

                    //wrap the date in a new tag
                    newDate += '' + newDateTime[dateIndex] + '';

                    //append the date and month back into their columns
                    $('.upcoming-column.left h1', this).html(newDate);	
                });
            }

            if( $('.hp').length > 0 ){
            	$('.safari #upcoming-events .ui-articles > li .ui-article').css('min-height', 0);
            	$('.safari #upcoming-events .ui-articles > li').each(function( liIndex, li ){
                	$('.ui-article', li).css('min-height', $(li).height());
                });
            }

            $(window).resize(function(e){
            	if( $('.hp').length > 0 ){
                	$('.safari #upcoming-events .ui-articles > li .ui-article').css('min-height', 0);
                    $('.safari #upcoming-events .ui-articles > li').each(function( liIndex, li ){
                        $('.ui-article', li).css('min-height', $(li).height());
                    });
                }
            });
        },
        "HeadlineCards": function(){
        	if( $('#headlines-scroller .app.headlines').length > 0 ){
                $('#headlines-scroller .app.headlines .ui-articles > li').each(function(k2, v2){
                    if(!$('.ui-article', v2).hasClass('background-set')){
                        $('.ui-article', v2)
                            .append('

')
                            .wrap('
');

                        if( $('.ui-article-thumb img', v2).attr('src') == undefined && $('.ui-article-thumb img', v2).attr('src') == "../../" ){
                            $('.card-info', v2).addClass('no-thumb');
                        }else{
                        	// Changed old article thumb selector from a.ui-article-thumb to a classed selector system to resolve issues created by the newest system update. - Jeremy K Case #03928886
                        	$('.ui-article-thumb', v2).addClass('old-thumb').before('
' + $('.ui-article-thumb', v2).html() + '');
                        	$('.ui-article-thumb.old-thumb', v2).remove();
                        } 

                        $('.card-info > div', v2).append($('.ui-article-title', v2));
                        $('.card-info > div', v2).append($('.ui-article-description', v2));

                        $('.ui-article', v2).addClass('background-set');
                    }
                });
            }else{
                $('#headlines-scroller')
                    .addClass('no-headlines')
                    .css('height', '');
            }
        },
        "ToggleHeadlineCards": function( obj ){
        	if( $(obj).hasClass('open-menu') || $(obj).hasClass('animating') ){
                $(obj).removeClass('open-menu animating').attr('ariahidden', 'true');

                $('.card-info', obj).stop(true, true)

                $('.ui-article-description', obj).fadeOut(200, function(){
                	$('.card-info', obj).animate({
                        'height': $('.ui-article-title', obj).height() + 27
                    }, 300, function(){
                    	$(obj).removeClass('open-menu animating')
                    });
                });

                $('.show-on-hover', obj).attr('aria-hidden', 'true');
            }else{
            	$(obj).addClass('animating');

                $('.card-info', obj).animate({
                	'height': $(obj).height()
                }, 300, function(){
                	$(obj).addClass('open-menu');
                	$('.ui-article-description', obj).fadeIn(200);
                });

                $('.show-on-hover', obj).attr('aria-hidden', 'false');
            }
        }
	};

