            this.SocialMedia();
            this.GoogleCalendars();

            $(window).load(function(){ 
                if($(".chrome").length) {
                	$(".cs-fullscreen-video-button.play-button").click();
                }
                //Added by Marie - Case 00171272
				$("
Barrington 220 School District").prependTo("#gb-mystart .sw-mystart-dropdown.schoollist .sw-dropdown-list"); 

                $("#hp-multimedia-content-outer").css("padding-top", $("#gb-header-outer").outerHeight());

                CreativeTemplate.MegaChannelMenu();

                $("ul.sw-channel-list li.sw-channel-item").on('mouseenter', function (e) {
                    if ($(this).hasClass("mega-channel")) {
                        var elm = $('.sw-channel-dropdown', this);
                        var off = elm.offset();
                        var l = off.left;
                        var w = elm.width();
                        var h = elm.outerHeight() + 42;
                        var docH = $(window).height();
                        var docW = $(window).width();

                        if($(".mega-menu-top", this).length) {
                            var menuHeight = $(".mega-menu-bottom", this).height() - $(".mega-menu-top", this).height();                            
                        } else {
                            var menuHeight = $(".mega-menu-bottom", this).height();
                        }

                        var isEntirelyVisible = (h <= docH);

                        if (!isEntirelyVisible) {
                            if($(".mega-menu-top", this).length) {
                                $(".mega-channel-cat-outer", this).css("max-height", docH - menuHeight - 117);
                            } else {
                                $(".mega-channel-cat-outer", this).css("max-height", docH - menuHeight + 92);
                            }
                        } else {
                            $(".mega-channel-cat-outer", this).removeAttr("style");
                        }
                    }
                });

                $("ul.sw-channel-list li.sw-channel-item").on('mouseleave', function (e) {
					$(".mega-channel-cat-outer", this).removeAttr("style");
                });
            }).resize(function(){ 
                CreativeTemplate.WindowResize(); 
            });   
      },
      "WindowResize": function() {
            $("#hp-multimedia-content-outer").css("padding-top", $("#gb-header-outer").outerHeight());
      },

      "GoogleCalendars": function() {
      	//DOC get list google calendars
		var gCalsList = 'district220@bsd220.org, boe220@bsd220.org, bhs220@bsd220.org, prairie220@bsd220.org, station220@bsd220.org, countryside220@bsd220.org, grove220@bsd220.org, elc220@bsd220.org, hough220@bsd220.org, lines220@bsd220.org, northbarrington220@bsd220.org, rose220@bsd220.org, roslyn220@bsd220.org, sunnyhill220@bsd220.org';
		gCalsList = gCalsList.replace(/\s+/g, '');
		var gCals = gCalsList.split(",");

        //DOC set calendar total
        if(gCals.length == 1) {
            var total = gCals.length;
        } else {
            var total = gCals.length-1;
        }

        //DOC set number for callback loop
        var callbackNum = 0;

        //DOC loop through each calendar
		for(var i=0;i<=gCals.length-1;i++) {
			if(gCals[i] != "") {
				$("#gcal").append('
');
				$("#hp-google-calendar-"+i).gCalFlow({
					calid: gCals[i],
					apikey: 'AIzaSyAJp4IfARKnIu3dj0DuCdFp2sagUghD3P4',
					auto_scroll: false,
					maxitem: 3,
					no_items_html: 'There are no events to display.',
					daterange_formatter: function(sd, ed, ad) {
						var monthname = [ "Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Sept", "Oct", "Nov", "Dec" ];
						var newDate = sd.getDate();
						var newMonth = (sd.getMonth()+1);

						if(newDate.toString().length == 1) {
							newDate = "0"+newDate.toString();
						} 
						if(newMonth.toString().length == 1) {
							newMonth = "0"+newMonth.toString();
						} 
						return "" + monthname[sd.getMonth()] + " " + sd.getDate() + "";  
					},
					callback: function() {                                                                        
						if(callbackNum == total) {
                          //DOC reformat dates
                          $(".gcf-item-date-block").each(function() {
                            var dateText = jQuery.trim($(this).html());
                            dateText = dateText.replace("[", "").replace("]", "");
                            $(this).html(dateText);
                          });

						  //DOC cleanup HTML 
                          $(".gCalFlow").each(function() {
                          	$(".gcf-last-update-block", this).remove();

                            if($(".gcf-no-items", this).length) {
                                $(this).remove();
                            }
                          });

                          //DOC remove empty description and change link
                          $(".gcf-item-block").each(function() {
                            $(".gcf-item-body-block", this).remove();
                            var newHref = $('a', this).attr('href');
                            newHref = newHref + '&ctz=America/Chicago';
                            $('a', this).attr('href', newHref);
                          });

    					  //DOC sort events by date
                          gCalSort(3);

                          if(!$(".gcf-item-block").length) {
                            $("#hp-multimedia-calendar").hide();
                          }

                          $(".gcf-item-block").each(function() {
                            if($(this).index() >= 3) {
                                $(this).remove();
                            }
                          });

                          $("#hp-google-calendar").show();
					    }

                        callbackNum++;
					}
				});
			}
		}
      },

      "Mystart": function() {
