      "StreamingVideo": function() {
            var _this = this;

            //Variables
            var mobilePhoto = '/cms/lib/IL01001296/Centricity/Template/GlobalAssets/images///background image/District.jpg';
            $("html").addClass("cs-streaming-video");

            if($.trim(mobilePhoto) == "" || mobilePhoto.indexOf("default-man.jpg") > -1) {
                mobilePhoto = "/cms/lib8/IL01001296/Centricity/Template/5/background-photo.jpg";
            }

            $("#hp-multimedia-outer").fullScreenRotator({
                "videoSource" : "vimeo", // OPTIONS ARE: youtube, vimeo
                "videoID": '373251944', // YouTube and Vimeo ids are set as default in the script
                "fullScreenBreakpoints" : [], // OPTIONS ARE [960, 768, 640, 480, 320]
                "showControls" : true,
                "mobileBackgroundPhoto" : mobilePhoto,
                "mobileBackgroundPhotoBreakpoint" : 480, // OPTIONS ARE 768, 640, 480, 320
                "onReady" : function(props) {
                    // ADD POSITIONING STYLES
                    var dynStyleSheet = document.createElement('style');
                    if(dynStyleSheet) {
                        dynStyleSheet.setAttribute('type', 'text/css');
                        var head = document.getElementsByTagName('head')[0];

                        if(head) {
                            head.insertBefore(dynStyleSheet, head.childNodes[0]);
                        }

                        var dynStyles = 'iframe#cs-fullscreen-video {' +
                                            'top: calc(((100vw * .4) - (100vw * ' + (props.height / props.width) + ')) / 2);' +	
                                        '}';

                        var rules = document.createTextNode(dynStyles);

                        if(dynStyleSheet.styleSheet){ // IE
                            dynStyleSheet.styleSheet.cssText = dynStyles;
                        } else {
                            dynStyleSheet.appendChild(rules);
                        }
                    }

                    $(".cs-fullscreen-video-buttons").appendTo($("#hp-multimedia-content"));
                    $(".cs-fullscreen-video-button.play-button .cs-fullscreen-video-control-text").text("Play Video");
                    $(".cs-fullscreen-video-button.pause-button .cs-fullscreen-video-control-text").text("Pause Video");
                    $(".cs-fullscreen-video-button.mute-button, .cs-fullscreen-video-button.unmute-button").attr({"tabindex": -1, "aria-hidden": true});

                    $(".cs-fullscreen-video-button.mute-button").click();
                    $(".cs-fullscreen-video-button.play-button").click();
                },
                "onStateChange" : function(props) { }
            });
		},
        "GlobalIcons": function() {
			var globalIcons = [
                {
                    "show": true,
                    "icon": 'Student Program/15.png',
                    "text": 'Lunch Menus',
                    "link": 'https://www.barrington220.org/domain/79',
                    "target": '_self'
                },
                {
                    "show": true,
                    "icon": '/cms/lib/IL01001296/Centricity/Template/GlobalAssets/icons/safety220.png',
                    "text": '#Safety220',
                    "link": '/safety220',
                    "target": '_self'
                },
                {
                    "show": true,
                    "icon": 'Site Utility/42.png',
                    "text": 'District Calendar',
                    "link": '/districtcalendar',
                    "target": '_self'
                },
                {
                    "show": true,
                    "icon": 'Education Portal/19.png',
                    "text": 'Infinite Campus',
                    "link": '/infinitecampus',
                    "target": '_self'
                }

            ];

            $.each(globalIcons, function(index, icon) {
                if(icon.show) {
                    var iconImage = "", defaultIconImage = "", iconTextClass = "", iconType = "", iconCat = "", iconChoice = "", iconSelection = "", splitSelection = "", globalIcons = "";	
                 	iconImage = $.trim(icon.icon);

					// DETERMINE IF THE ICON IS A SYSTEM ICON, CUSTOM IMAGE, OR EMPTY
					iconType = (iconImage != "") ? iconImage.split("/") : "none";
					if(iconType != "none" && $.inArray("cms", iconType) != -1) {
						iconType = "image";
					} else if(iconType != "none") {
						iconType = "system";
					}

                    // ICON IS FROM THE SYSTEM
                    if(iconType == "system") {
						splitSelection = iconImage.split("/");
						iconCat = $.trim(splitSelection[0]);
						iconCat = iconCat.toLowerCase().replace(/\s+/g, "-"); // LOWERCASE THE CATEGORY NAME AND REPLACE ALL SPACES WITH DASHES
						iconChoice = splitSelection[1].split(".");
						iconChoice = iconChoice[0];
                        iconSelection = iconCat + "-" + iconChoice;

					    $("#gb-global-tabs").append('
' + icon.text + '');
                    }

                    // ICON IS A CUSTOM IMAGE
                    else {         
                        $("#gb-global-tabs").append('
' + icon.text + '');
					}
                }
            });
        },
