	$(window).load(function(){
		calendarListView();
		searchText();

        //DOC overwrite system functionality
        $(".sw-channel-item").off();
        var settings = {
            menuHoverClass: 'hover'
        };
        var top_level_links = $('[id="channel-navigation"]').find('> li > a');
        $(top_level_links).hover(function () {
            //hide open menus
            hideChannelMenu();
            if ($(this).parent('li').find('ul').length > 0) {
                $(this).parent('li').addClass(settings.menuHoverClass).find('ul').css('display', 'block');
            }
        });
        function hideChannelMenu() {
            $('li.sw-channel-item.' + settings.menuHoverClass).attr('aria-hidden', 'true').removeClass(settings.menuHoverClass).find('ul').css('display', 'none').find('a').attr('tabIndex', -1);
        }

		//DOC channel bar mods
        $(".sw-channel-list").attr("tabindex", 0);
		$("li.sw-channel-item").each(function(){
			if(!$(".sw-channel-dropdown li", this).size()) {
				$(".sw-channel-dropdown", this).addClass("hide");
			}
		});

        //DOC collapse channel bar
        $("#channel-navigation").hover(function() {
        }, function() { 
        	hideChannelMenu();
            $("li.sw-channel-item").removeClass("hover-line");
        });
        $("#channel-navigation").on('focusout', function() {
            var $elem = $(this);
            setTimeout(function() {
                var hasFocus = !! ($elem.find(':focus').length > 0);
                if (! hasFocus) {
        			hideChannelMenu();
            		$("li.sw-channel-item").removeClass("hover-line");
                }
            }, 10);
        });
        $("body").on("click touchstart", function() {
        	hideChannelMenu();
            $("li.sw-channel-item").removeClass("hover-line");
        });
        $("#channel-navigation").on("click touchstart", function(e) {
            e.stopPropagation();
        });
	});

