	function pgScreenSize(){
		var getWidth = $(window).width();
		if (getWidth >= 1024) { // 960+
			$("#hp-multimedia-content").height($(window).height()-100);
		} if (getWidth >= 768 && getWidth < 1024) { // 768+
			$("#hp-multimedia-content").height($(window).height()-100);
            $("#hp-multimedia-outer").css("top", 0);
			appView();
		} if (getWidth >= 640 && getWidth < 768) { // 640+
			$("#hp-multimedia-content").height("auto");
            $("#hp-multimedia-outer").css("top", 0);
			appView();
		} if (getWidth >= 480 && getWidth < 640) { // 480+
			$("#hp-multimedia-content").height("auto");
            $("#hp-multimedia-outer").css("top", $("#gb-header-outer").outerHeight()+30);
			appView();
		} if (getWidth >= 320 && getWidth < 480) { // 320+
			$("#hp-multimedia-content").height("auto");
            $("#hp-multimedia-outer").css("top", $("#gb-header-outer").outerHeight()+30);
			appView();
		}
	}

	function appView() {
		//DOC make homepage apps collapsible for small mobile devices
		$("#hp-content div.ui-widget-header").click(function(){
			var clickedApp = $(this).parent().parent().attr("id");

			$("div.ui-widget.app").removeClass("active");
			$(this).parent().addClass("active");

			$("html, body").animate({
				scrollTop: $("#" + clickedApp).offset().top
			}, 500, function(){
				$("html, body").stop(true, false);
			});
		});
	}	

	function dynamicContent() {

		// ********** MYSTART ********** //
		$("#sw-mystart-right").prepend("
Contact 
Site Map");
		$("#sw-mystart-outer #sw-search-button").html("");
		$("#gb-page").after($("#sw-mystart-outer"));

        $("#sw-mystart-outer #sw-mystart-search").prependTo("#gb-search-outer");

		// ********** CHANNEL BAR ********** //
		//DOC add first class to first channel item
		$("li.sw-channel-item").first().addClass("first");
		//DOC add last class to last channel item
		$("li.sw-channel-item").last().addClass("last");
		//DOC wrap dropdown with inner div
		$(".sw-channel-dropdown").wrapInner("
");

		// ********** BODY ********** //
		var primaryColor = "rgba("+hexToRgb('#9D0000')+", 0.1)";
		//DOC set background color
		$("#hp-multimedia-scroll-background, .gb-footer-scroll-background").css("background-color", primaryColor);

		var isInIFrame = (window.location != window.parent.location);
		if(isInIFrame==true){
			if("false" == "true") {
				$("#hp-multimedia-outer img").show();
