$('#aoic-banner .bannerStartStopControl').click( function(event) {
  event.preventDefault();
  if($(this).hasClass('pause')) {
  $(this).removeClass('pause');
  $(this).addClass('play');  
  $(this).find('em').text('Play');
  $('#aoic-banner .slides').slick('slickPause');
} else {
  $(this).removeClass('play');
  $(this).addClass('pause');  
  $(this).find('em').text('Pause');
  $('#aoic-banner .slides').slick('slickPlay');
  }
});

