//script to create sticky navr 
$(function(){
    $('#menubar').appendTo('#header');
    createSticky($("#header"));
});

function createSticky(sticky) {
    var headerHeight = $('.sm-header').outerHeight();
    var headercontainerHeight = $('div#header > container').height();
    if (typeof sticky != "undefined") {

        var pos = sticky.offset().top + headercontainerHeight + 10,
        win = $(window);

        win.on("scroll", function() {
            win.scrollTop() >= pos ? ( 
                sticky.addClass("sm-header") && 
                $('#content-wrapper').css('margin-top', "169px")
                ) 
            : 
            (
                sticky.removeClass("sm-header") && 
                $('#content-wrapper').css('margin-top', 0)
                );
        });         
    }
}

