div.script:
                        function googleTranslateElementInit() {
                                new google.translate.TranslateElement({  
                                includedLanguages:'sq,ar,bs,zh-CN,zh-TW,cs,da,nl,en,fi,fr,de,el,ht,iw,hmn,hu,ga,it,ja,ko,lo,la,lv,lt,mk,mn,ne,no,pl,pt,ro,ru,sr,so,es,su,sw,sv,th,tr,uk,vi,cy,yi',          			
                                pageLanguage: 'en',
                                layout: google.translate.TranslateElement.InlineLayout.SIMPLE,
                                autoDisplay: false
                            }, 'google_translate_element');
                            $('.goog-te-gadget-simple').css('font-size', '10px');
							$('.goog-te-menu-frame').attr('title', 'Google Translator');
							// begin accessibility compliance, requires jQuery
							$('img.goog-te-gadget-icon').attr('alt','');
							$('div#goog-gt-tt div.logo img').attr('alt','translate');
							$('div#goog-gt-tt .original-text').css('text-align','left');
							$('.goog-te-gadget-simple .goog-te-menu-value span').css('color','#000');
							$('.goog-te-combo').attr('title','Select A Language');
							$("html").attr("lang", pageLanguage);
                        }

td.script:
function ec_713423(ecid,display){
	var displayDiv = document.getElementById('ec_'+ecid+'_content').style.display;
	if(displayDiv == 'block'){
		document.getElementById('ec_'+ecid+'_content').style.display = 'none';
		document.getElementById('ec_'+ecid+'_minus').style.display = 'none';
		document.getElementById('ec_'+ecid+'_plus').style.display = 'block';
		$('#ec_'+ecid+'_shell').removeClass('expand_collapse_shell_open');
	}else{
		document.getElementById('ec_'+ecid+'_content').style.display = 'block';
		document.getElementById('ec_'+ecid+'_minus').style.display = 'block';
		document.getElementById('ec_'+ecid+'_plus').style.display = 'none';
		$('#ec_'+ecid+'_shell').addClass('expand_collapse_shell_open');
	}
	
	/*document.getElementById('ec_'+ecid+'_content').style.display = display;
	if(display=='block') {
		document.getElementById('ec_'+ecid+'_minus').style.display = 'block';
		document.getElementById('ec_'+ecid+'_plus').style.display = 'none';
	}
	else {
		document.getElementById('ec_'+ecid+'_minus').style.display = 'none';
		document.getElementById('ec_'+ecid+'_plus').style.display = 'block';
	}*/
}

