        	pgScreenSize(); 
            calendarListView(); 
            $('#gb-sticky-right-sidebar').css('top', $('#gb-channel-list-outer').height() + 40);
            if( $('.safari').length > 0 ) safariChannelBarFix();
        });

        SLM(); //calls rsMenu

		// RESPONSIVE PAGELIST MENU
		$("body").csRsPagelistMenu({
		    "breakPoint"            : 480, // SYSTEM BREAK POINTS - 768, 640, 480, 320
			"slideDirection"        : "left-to-right", // OPTIONS - left-to-right, right-to-left
			"primaryColor"          : "#2C363E", // DEFAULT - #2C363E
			"secondaryColor"        : "#222A30", // DEFAULT - #222A30
			"accentColor"           : "#B5BBBF", // DEFAULT - #B5BBBF
			"menuButtonParent"      : "#sp-content",
			"showMenuBtnText"       : "yes",
			"menuBtnText"           : "PAGES",
			"siteID"                : "4"
		});

		pgScreenSize();
		useChannelForSection();
		dynamicContent();
		customDropdowns('point-up', 'point-down');	

        fitTextSetup();
        addFramesetTranslate();
        setupCustomColors();

        megaChannels();

        checkSlideshow();

        $('#gb-sticky-right-sidebar').css('top', $('#gb-channel-list-outer').height() + 40);

        $(window).scroll(checkSlideshow);
	});	

	function pgScreenSize(){
		var getWidth = $(window).width();

		if (getWidth >= 1024) { //Desktop View
			multimediaRotator();
            resizeSideBar();

            if( $('.sp').length > 0 ){	
            	$('#sp-content-outer, #spn-content-outer').css('padding-top', $('#gb-logo').height() + 40);
                $('#gb-channel-list-outer').css('padding-left', $('#gb-logo').outerWidth());
            }
		}else{ //Mobile BPs
			multimediaRotator();
			appView();
		}
	}

    function resizeSideBar(){
    	$('#gb-sticky-left-sidebar-outer > div').css('height', $(window).height());

        var availChannelSpace = $(window).outerHeight() - ($('#gb-custom-mystart').outerHeight() + $('#gb-sidebar-logo-cont').outerHeight() + 100 /*sidebar-footer-height*/);

        $('#gb-channel-list-outer').css('max-height', availChannelSpace);
    }

	function multimediaRotator(){
		if(!$("#sw-content-container10.ui-hp .rs-gallery-container").length){
			// MULTIMEDIA ROTATOR
			$("#sw-content-container10.ui-hp div.ui-widget.app.cs-rs-multimedia-rotator").csRsMultimediaRotator({
						"imageWidth"              : 1400,
		                "imageHeight"             : 719,
		                "efficientLoad"           : "yes",
		                "appHeader"               : "no",
		                "titles"                  : "yes",
		                "captions"                : "yes",
		                "galleryOverlay"          : "no",
		                "linkedElement"           : [], // ["image", "title", "overlay", "all"]
		                "playPause"               : "no",
		                "mainControls"            : "yes",
		                "contMoreLink"            : "no",
		                "contMoreLinkText"        : "Read More",
		                "descMoreLink"            : "yes",
		                "descMoreLinkText"        : "Learn More",
		                "bullets"                 : "no",
		                "bulletControls"          : "no",
		                "thumbnails"              : "no",
		                "thumbnailWidth"          : 122,
		                "thumbnailHeight"         : 53,
		                "thumbnailSlideDirection" : "horizontal", // horizontal or vertical
		                "hoverPause"              : "yes",
		                "autoRotate"              : "yes",
		                "fullScreenRotator"       : "no", // image dimensions work well at 1500x915
		                "fullScreenBreakpoints"   : ["960"], // OPTIONS ARE ["960", "768", "640", "480", "320"]
		                "fullScreenVideo"         : "no",
		                "transitionType"          : "fade", // fade or slide
		                "transitionSpeed"         : 3,
		                "transitionDelay"         : 8,
		                "onImageLoad"             : function() { },
		                "allImagesLoaded"         : function() { },
		                "onThumbnailLoad"         : function() { },
		                "allThumbnailsLoaded"     : function() { },
		                "onTransitionStart"       : function() { },
		                "onTransitionEnd"         : function() { },
		                "watchVidClick"           : function() { },
		                "closeVidClick"           : function() { },
		                "allLoaded"               : function() { 

                        	if( $('#hp-slideshow .rs-gallery-control.back').length > 0 ){
                            	$('#hp-slideshow .rs-gallery-control.back').after($('#hp-slideshow .rs-gallery-desc'));

                            	$('#hp-slideshow .rs-gallery-control.back')
                                	.clone(true)
                                    .attr('tabindex', -1)
                                    .addClass('mobile-ctrl')
                                    .appendTo('#hp-slideshow .rs-gallery-desc');
                                $('#hp-slideshow .rs-gallery-control.next')
                                	.clone(true)
                                    .attr('tabindex', -1)
                                    .addClass('mobile-ctrl')
                                    .appendTo('#hp-slideshow .rs-gallery-desc');
                            }

                        }
			});
		}
	}

	function modEvents(){
	    if (!$(".upcomingevents .joel-month").length) {
	        $(".upcomingevents").modEvents({
	            columns     : "yes",
	            monthLong   : "no",
	            dayWeek     : "no"
	        });
	        eventsByDay(".upcomingevents .ui-articles");
	    }

        if( $('.hp').length > 0 ){
        	$('#hp-tabbed-app-area .upcomingevents').each(function(k, v){
            	$('.ui-articles li', v).filter(":gt(4)").remove();
            });
        }
	}

	function eventsByDay(container) {
	    var countLi = 0;
	    $(".ui-article", container).each(function(){
	        if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
	            var moveArticle = $(this).html();
	            $(this).parent().prev().children().children().next().append(moveArticle);
	            $(this).parent().remove();
	        };
	    });

		$(".ui-article", container).each(function(){
			var newDateTime = $('.upcoming-column.left h1', this).html().toLowerCase().split("");
			var newDate = '';
			var dateIndex = ((newDateTime.length > 2) ? 2 : 1); //if the dayWeek is set to yes we need to account for that in our array indexing

			//add a zero to the date if it is a single digit
			if( newDateTime[dateIndex].length == 1 ) newDateTime[dateIndex] = "0" + newDateTime[dateIndex];

			//if we have the day of the week make sure to include it.
			if( dateIndex > 1 ){ 
				newDate += newDateTime[0] + "";
			}

			//add in the month
			newDate += newDateTime[dateIndex - 1] + ''; //the month is always the in the left array position to the date

			//wrap the date in a new tag
			newDate += '' + newDateTime[dateIndex] + '';

			//append the date and month back into their columns
			$('.upcoming-column.left h1', this).html(newDate);	


			//add an ALL DAY label if no time was given
			$('.upcoming-column.right .ui-article-description', this).each(function(){
				if( $('.sw-calendar-block-time', this).length < 1 ){ //if it doesnt exist add it
