                                    '
' +
                                        'Portuguese' +
                                    '' +
                                    '
' +
                                        'Russian' +
                                    '';

        var pageHost = window.location.host;
        if(pageHost != "translate.googleusercontent.com"){				
            $("#gb-translate-dropdown .dropdown").append(translateDropdown);
        }

    }

    var csSlideshowDescFixed = false;

    function checkSlideshow() {
      if($(".hp").length && $(window).width() <= 1023) {
          if ($(window).scrollTop() <= $("#hp-slideshow").offset().top + $("#hp-slideshow").height()) {
              if(csSlideshowDescFixed) {
                  $("#hp-slideshow .rs-gallery-desc").removeAttr("style");

                  csSlideshowDescFixed = false;
              }
          } else {
              if(!csSlideshowDescFixed) {
                  $("#hp-slideshow .rs-gallery-desc").css({
                      "height": $("#hp-slideshow .rs-gallery-desc").height(),
                      "overflow": "hidden"
                  });

                  csSlideshowDescFixed = true;
              }
          }
      }
    }

    function fitTextSetup(){
    	$('#hp-link-cards-cont .card-title-slide h1').fitText(1.2, {minFontSize: '30px', maxFontSize: '50px;'});
    }

    function SLM() {
        $.schoolListMinibase({
            'miniBaseDomainID': 4,
            'miniBaseModuleInstanceID': 4789,
            'cache': true,
            'schoolListReady': function(schoolList) {
                /*
                The returned value is an array of objects and each object is a school record.
                The fields are: schoolList.schoolName, schoolList.schoolType, schoolList.schoolUrl, schoolList.schoolAlias, schoolList.schoolVisible
                */

                var schools = '';

                for(var i in schoolList) {
                    if(schoolList[i].schoolVisible.toLowerCase() == 'true') {
                        schools += '
' + schoolList[i].schoolName + '';
                    }
                }

                $('#school-dropdown-items').html(schools);
                $('.sw-mystart-dropdown.schoollist ul li').clone().appendTo('#school-list');
                rsMenu(schoolList);
            }
        });
    }

    function rsMenu(schoolList) {
        var mobileSchools = '';

        for(var i in schoolList) {
            if(schoolList[i].schoolVisible.toLowerCase() == 'true') {
                mobileSchools += '1,' + schoolList[i].schoolName + ',' + schoolList[i].schoolUrl + ', _blank;';
            }
        }

        $("body").csRsMenu({
            "breakPoint"            : 768, // SYSTEM BREAK POINTS - 768, 640, 480, 320
			"slideDirection"        : "left-to-right", // OPTIONS - left-to-right, right-to-left   
			"primaryColor"          : "#292f30", // DEFAULT - #2C363E
			"secondaryColor"        : "#1b2021", // DEFAULT - #222A30
			"accentColor"           : "#b8bcbb", // DEFAULT - #B5BBBF
			"menuButtonParent"      : "#gb-custom-mystart",
			"showMenuBtnText"       : "yes",
			"menuBtnText"           : " MENU",
			"showSchools"           : "no", /* Changed from yes by Jeremy K - Case #02982619 11.20.17 */
			"showTranslate"         : "yes",
			"translateId"           : "none",
			"showAccount"           : "yes",
            "extraMenuOptions"      : "Schools", // EXTRA MENU HEADING NAMES SEPARATED BY A ;
            "extraMenuItems"        : mobileSchools, // EXTRA MENU BUTTONS - STRUCTURE IS: MENU-OPTION-NUMBER, ITEM-NAME, ITEM-URL, ITEM-TARGET;
            "siteID" : "",
            "allLoaded" : function(){ }
        });
    }

