                    var srcSplitLen = srcSplit.length;

                    if(jQuery.trim(megaMenuData[i][1][0]) != "" && imgSrc != "" && srcSplit[srcSplitLen - 1] != "default-man.jpg"){
                        // ADD CAPTION
                        var captionColumn = '
