    });

    var CreativeTemplate = {
    	// PROPERTIES
        "KeyCodes": { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 },
        "IsMyViewPage": false,

        // METHODS
        "Init": function() {
            // FOR SCOPE
            var _this = this;

            this.SetTemplateProps();
            this.JsMediaQueries();
            this.Header();
            this.DrawCanvas();
            this.ChannelBar();
            this.QuickFindIcons();
            this.GlobalIcons();
            this.Body();
            this.DynStyles();
            this.ModEvents();
            this.FunFacts();
            this.Footer();
            this.SocialMediaIcons();
            this.Slideshow();
            this.RsMenu();
            this.AppAccordion();
            this.Search();

            $(window).load(function(){ _this.WindowLoad(); });
            $(window).resize(function(){ _this.WindowResize(); });
        },

        "SetTemplateProps": function() {
        	// MYVIEW PAGE CHECK
            if($("#pw-body").length) this.IsMyViewPage = true;
        },

        "WindowLoad": function() {

        },

        "WindowResize": function() {
            this.JsMediaQueries();
        },

        "JsMediaQueries": function() {
            switch(this.GetBreakPoint()) {
                case "desktop": 

                break;
                case "768":

                break;
                case "640":

                break;
                case "480":

                break;
                case "320":

                break;
            }
        },

        "MyStart": function() {
        	// MYSTART BUTTONS AND DROPDOWNS

            $("#gb-mystart").append('
' + $(".sw-mystart-button.signin").clone().html() + '');

            if($(".sw-mystart-button.register").length) {
            	$("#gb-mystart").append('
' + $(".sw-mystart-button.register").clone().html() + '');
            }


            //Adam - 12.13.17 - #02994037
            $("#gb-mystart").append('
Email');

            // SCHOOL LIST
            var schools = "";

            $("#gb-mystart").prepend('' +
            	'
' +
                    '
' +
                        'Schools' +
                    '' +
                    '
' +
                        '' +
                        	$(".sw-mystart-dropdown.schoollist .sw-dropdown-list").html() +
                        '' +
                    '' +
                '' +
             '');

        	// ADD TRANSLATE
        	$("#gb-mystart").creativeTranslate({
                "type": 1,
                "languages": [
                	["Spanish", "Espaol", "es"],
                    ["Arabic", "", "ar"],
                    ["Chinese Simplified", "", "zh-CN"],
					["Chinese Traditional", "", "zh-TW"],
                    ["French", "franais", "fr"],
					["German", "Deutsche", "de"],
                    ["Italian", "italiano", "it"],
					["Japanese", "", "ja"],
                    ["Korean", "", "ko"],
                    ["Portuguese", "portugus", "pt"],
                    ["Russian", "", "ru"]
                ],
                "advancedOptions": {
                    "addMethod": "prepend",
                    "customElement": {
                        "useCustomElement": true, 
                        "translateItemsList": true,
                        "customElementMarkup": '
' +
                                                  '
Translate' +
