            $(document).on("click keydown", "#gb-quick-find-toggle", function(e) {
            	if(template.AllyClick(e)) {
                	e.preventDefault();

                    $("#gb-quick-find-outer").addClass("open").attr("aria-hidden", "false");

                    if(e.type == "keydown") {
                    	setTimeout(function() {
                        	$("#gb-quick-find-close").focus();
                        }, 500);
                    }
                }
            });

            $(document).on("click keydown", "#gb-quick-find-close", function(e) {
            	if(template.AllyClick(e)) {
                	e.preventDefault();

                    $("#gb-quick-find-outer").removeClass("open").attr("aria-hidden", "true");
                    $("#gb-quick-find-toggle").focus();
                }

                if(e.type == "keydown" && e.keyCode == template.KeyCodes.tab && e.shiftKey) {
                	e.preventDefault();

                    $("#gb-search-button").focus();
                }
            });

            $(document).on("keydown", "#gb-search-button", function(e) {
            	if(e.keyCode == template.KeyCodes.tab && !e.shiftKey) {
                	e.preventDefault();

                    $("#gb-quick-find-close").focus();
                }
            });

            $(document).on("keydown", "#gb-quick-find-outer", function(e) {
            	if(e.keyCode == template.KeyCodes.esc) {
                	e.preventDefault();

                    $("#gb-quick-find-outer").removeClass("open").attr("aria-hidden", "true");
                    $("#gb-quick-find-toggle").focus();
                }
            });

            $(document).on("keydown", function(e) {
            	setTimeout(function() {
                    if(!$("#gb-quick-find-toggle").is(":focus") && $("#gb-quick-find-outer").hasClass("open") && !$("#gb-quick-find-outer").find(":focus").length) {
						$("#gb-quick-find-outer").removeClass("open").attr("aria-hidden", "true");
                    }
                }, 500);
            });
        },

