        "Body": function() {
            // AUTO FOCUS SIGN IN FIELD
            $("#swsignin-txt-username").focus();

            // MOVE HEADLINES MORE BUTTON
            $(".ui-widget.app.headlines").each(function(){
            	if($("a.more-link", this).length) {
                    var appHeader = $.trim($(".ui-widget-header", this).text()).replace(/[\n\r\t]/gi, "");
                    var viewAllText = 'View All Headlines';
                    if(appHeader != "") {
                        viewAllText = 'View All ' + appHeader + '';
                    }
                    $("a.more-link span", this).html(viewAllText);
                    $(".ui-widget-header", this).removeClass("ui-helper-hidden").addClass("has-more-link").append($("a.more-link", this).clone());
                    $(".ui-widget-header a.more-link", this).removeAttr("id");
                    $(".more-link-under", this).remove();
                    $("div.ui-widget-detail", this).append($("div.ui-widget-detail a.more-link", this));
                }
            });

            // MOVE OTHER MORE BUTTONS
            $("a.more-link").each(function() {
            	if(!$(this).closest(".ui-widget.app").hasClass("headlines")) {
                	var appHeader = $.trim($(this).closest(".ui-widget.app").find(".ui-widget-header").text()).replace(/[\n\r\t]/gi, "");
                    var viewAllText = 'View All';
                    if(appHeader != "") {
                        viewAllText = 'View All ' + appHeader + '';
                    }
                    $("span", this).html(viewAllText);
                    $(this).closest(".ui-widget.app").find(".ui-widget-detail").append($(this));
                    $("+ .more-link-under", this).remove();
                }
            });

            // MOVE FOLLOW BUTTONS
            $(".app-level-social-follow").each(function() {
            	$(this).closest(".ui-widget.app").find(".ui-widget-footer").addClass("ui-clear").prepend($(this));
            });

            // APPLY RESPONSIVE DIMENSIONS TO CONTENT IMAGES
            $("div.ui-widget.app .ui-widget-detail img")
                .not($("div.ui-widget.app.cs-rs-multimedia-rotator .ui-widget-detail img"))
                .not($("div.ui-widget.app.gallery.json .ui-widget-detail img"))
                .each(function() {
                    if ($(this).attr('width') !== undefined && $(this).attr('height') !== undefined) { // IMAGE HAS INLINE DIMENSIONS
                        $(this).css({"display": "inline-block", "width": "100%", "max-width": $(this).attr("width") + "px", "height": "auto", "max-height": $(this).attr("height") + "px"});
                    }
            });

            // ADJUST FIRST BREADCRUMB
            $("li.ui-breadcrumb-first > a > span").text("Home");

            // USE CHANNEL NAME FOR PAGELIST NAV HEADER IF ONE IS NOT PRESENT
            if(!$("div.sp.column.one .ui-widget-header h1").length) {
                $("div.sp.column.one .ui-widget-header").append("
COVID-19");
            }

            // ADD SPANS TO NAVIGATION APPS
            $("div.ui-widget.app.navigation li a").each(function() {
            	//$(this).html('' + $(this).text() + ''); changed text to html - Adam 10.6.17 - Case #02938567
            	$(this).html('' + $(this).html() + '');
            });

            // NAVIGATION APP HOVER
            $("div.ui-widget.app.navigation li a").hover(function() {
            	$(this).parent().addClass("hover");
            }, function() {
            	$(this).parent().removeClass("hover");
            }).focus(function() {
            	$(this).parent().addClass("hover");
            }).blur(function() {
            	$(this).parent().removeClass("hover");
            });
        },

        "DynStyles": function() {
            var dynStyleSheet = document.createElement('style');

            if(dynStyleSheet) {
                dynStyleSheet.setAttribute('type', 'text/css');
                var head = document.getElementsByTagName('head')[0];

                if(head) {
                    head.insertBefore(dynStyleSheet, head.childNodes[0]);
                }

                var dynStyles = '#hp-content-outer:before {' +
                                    'background: linear-gradient(to bottom, rgba(' + this.HexToRgb('#07a2c3') + ', .48) 0%,rgba(' + this.HexToRgb('#07a2c3') + ', 0) 100%);' +
                                '}';

                var rules = document.createTextNode(dynStyles);

                if(dynStyleSheet.styleSheet){ // IE
                    dynStyleSheet.styleSheet.cssText = dynStyles;
                } else {
                    dynStyleSheet.appendChild(rules);
                }
            }
        },

        "HexToRgb": function(h){
            var r = parseInt((this.CutHex(h)).substring(0,2),16);
            var g = parseInt((this.CutHex(h)).substring(2,4),16);
            var b = parseInt((this.CutHex(h)).substring(4,6),16);
            return r + ', ' + g + ', ' + b;
        },

        "CutHex": function(h) {
        	return (h.charAt(0)=="#") ? h.substring(1,7):h;
        },

        "ModEvents": function() {
            $(".ui-widget.app.upcomingevents").modEvents({
                columns     : "yes",
                monthLong   : "no",
                dayWeek     : "no"
            });

            $(".ui-widget.app.upcomingevents").each(function(){
            	$(".ui-article", this).each(function() {
                    if (!$("h1.ui-article-title.sw-calendar-block-date", this).length){
                        var moveArticle = $(this).html();
                        $(this).parent().prev("li").find(".upcoming-column.right").append(moveArticle);
                        $(this).parent().remove();
                    } else {
                        var month = $(".sw-calendar-block-date .joel-month", this).text();
                        $(".sw-calendar-block-date .joel-month", this).remove();
                        var date = $(".sw-calendar-block-date", this).text();
                        $(".sw-calendar-block-date", this).empty().html('' + month + '' + date + '');
                    }
                });

                var appHeader = $.trim($(".ui-widget-header", this).text()).replace(/[\n\r\t]/gi, "");
                var calendarLinkText = 'View All Events';
                if(appHeader != "") {
                	calendarLinkText = 'View All ' + appHeader + '';
                }
                $("a.view-calendar-link span", this).html(calendarLinkText);
            });
        },

        "FunFacts": function() {
        	if($(".hp").length) {
            	var funFacts = [
                	{
                    	"show": true,
                        "icon": "Classroom/15.png",
                        "text": "Over $5 Mil 2019 Scholarships"
                    }, 
                    {
                    	"show": true,
                        "icon": "Communication/19.png",
                        "text": "Level 5 TVAAS Systemwide Composite Score"
                    }, 
                    {
                    	"show": true,
                        "icon": "Classroom/11.png",
                        "text": "ACT Composite Score of 20.5"
                    }
                ];

                var facts = '';
                $.each(funFacts, function(index, fact) {
                	if(fact.show) {
                    	var iconType = fact.icon.split("/");
                        if($.inArray("cms", iconType) != -1) {
                            iconType = "image";
                        } else {
                            iconType = "system";
                        }

                        var icon = '';
                        if(iconType == "system") {
                        	var splitSelection = fact.icon.split("/");
                            var iconCat = $.trim(splitSelection[0]);
                            iconCat = iconCat.toLowerCase().replace(/\s+/g, "-");
                            var iconChoice = splitSelection[1].split(".");
                            iconChoice = iconChoice[0];
                            var iconSelection = iconCat + "-" + iconChoice;

                            icon = '';
                        } else {
                        	icon = '';
                        }

                    	facts += '
' + icon + '' + fact.text + '';
                    }
                });

                if(facts.length) {
                	$("#hp-fun-facts").html('' + facts + '');
                }
            }
        },

        "Footer": function() {
        	// FOR SCOPE
            var template = this;

        	// SET GOOGLE MAP IFRAME SRC
            var googleMap = "//maps.google.com/maps?q=" + encodeURIComponent("405 W College Street+Jonesborough+TN+37659") + "&output=embed&z=12";
            $("#gb-footer-google-map").html('');

            // GOOGLE MAP CLICK
            $(document).on("click keydown", "#gb-footer-google-map", function(e) {
                if(template.AllyClick(e)) {
                    e.preventDefault();
                    var mapURL = encodeURIComponent("405 W College Street Jonesborough TN 37659");
                    window.location = "//www.google.com/maps/place/" + mapURL;
                }
            });

            // ADD SITENAME
            var siteNameOne = $.trim("    ");
            var siteNameTwo = $.trim("    ");
            var siteNameBegin = "", siteNameEnd = "";
            if((siteNameOne == "") && (siteNameTwo == "")) {
                var splitLen = 2;
                var siteName = "Washington County Schools";
                siteName = siteName.split(" ");
                var siteNameLength = siteName.length;
                if(siteNameLength > 2){
                    siteNameEnd = $.trim(siteName.splice(-splitLen, siteName.length).toString().replace(/,/g, " "));
                } else {
                    siteNameEnd = $.trim(siteName.splice(-1, siteName.length).toString().replace(/,/g, " "));
                }
                siteNameBegin = $.trim(siteName.toString().replace(/,/g, " "));
                $(".gb-footer.two").prepend("
" + siteNameBegin + "
" + siteNameEnd + "");
            } else if((siteNameOne != "") && (siteNameTwo == "")) {
                $(".gb-footer.two").prepend("
" + siteNameOne + "");
            } else if((siteNameOne == "") && (siteNameTwo != "")) {
                $(".gb-footer.two").prepend("
" + siteNameTwo + "");
            } else if((siteNameOne != "") && (siteNameTwo != "")) {
                $(".gb-footer.two").prepend("
" + siteNameOne + "
" + siteNameTwo + "");
            }

            // FOOTER DISCLAIMER
            if(true) {
            	$("#gb-footer-statement").html('
Mission and Vision
MISSION: The Washington County Schools provide educational experiences through which every student will be prepared for their next steps in education and life.  VISION: All students in Washington County Schools have equitable access to high quality programs of instruction and support.');
            }

            // BACK TO TOP BUTTON
            $(document).on("click keydown", "#gb-back-to-top", function(e){
                if(template.AllyClick(e)) {
                	e.preventDefault();
                	$("html, body").animate({ 
                    	scrollTop: 0
                    }, "slow", function() {
                    	if(template.GetBreakPoint() == "desktop" || template.GetBreakPoint() == "768") {
                    		$("#channel-navigation > li:first-child > a").focus();
                        } else {
                        	$("#rs-menu-btn").focus();
                        }
                    });
                }
            });

            // MOVE Bb FOOTER STUFF
            $(".gb-schoolwires-footer.logo").html($("#sw-footer-logo").html());
            var schoolwiresLinks = '';
            schoolwiresLinks += '
' + $.trim($("#sw-footer-links li:eq(0)").html().replace("|", "")) + '';
            schoolwiresLinks += '
' + $.trim($("#sw-footer-links li:eq(2)").html().replace("|", "")) + '';
            schoolwiresLinks += '
' + $.trim($("#sw-footer-links li:eq(1)").html().replace("|", "")) + '';
            $(".gb-schoolwires-footer.links").append('' + schoolwiresLinks + '');
            $(".gb-schoolwires-footer.copyright").append($("#sw-footer-copyright").html());
        },

