	$(document).ready(function() {
		//encode filepath as suggested by Google and set src attribute of iframe to that encoded url
        var filePath = "https://www.wcde.org//cms/lib/TN02209007/Centricity/Domain/3126/COVID 19 Nutrition Department Statement-Updated-March 2020.pdf";
        
        if (filePath !== ''){
        	$("#doc-viewer-text-34569").removeClass("ui-helper-hidden");
        }
		
        filePath = encodeURIComponent(filePath);
       
        var url = "https://docs.google.com/viewer?url=" + filePath + "&embedded=true";
        
        $("#doc-viewer-34569").attr("src", url);
	});

