            // FOR SCOPE
            var _this = this;
            // FOR CAROUSEL ON RESIZE
            var resizeTimer;

            this.SetTemplateProps();
            this.Slideshow();
            this.CheckSlideshow();
            this.mmgTitles();
            this.MyStart();
            this.JsMediaQueries();  
            this.SchoolList();
            this.Translate();
