        "DropdownActions": function(params) {
            // FOR SCOPE
            var template = this;

            var dropdownParent = params.dropdownParent;
            var dropdownSelector = params.dropdownSelector;
            var dropdown = params.dropdown;
            var dropdownList = params.dropdownList;

            $(dropdownParent + " " + dropdownList + " a").attr("tabindex", "-1");

            // MYSTART DROPDOWN SELECTOR CLICK EVENT
            $(dropdownParent).on("click", dropdownSelector, function(e) {
                e.preventDefault();

                if($(this).parent().hasClass("open")){
                    $("+ " + dropdownList + " a").attr("tabindex", "-1");
                    $(this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
                } else {
                    $(this).attr("aria-expanded", "true").parent().addClass("open").find(dropdown).attr("aria-hidden","false").slideDown(300, "swing");
                }
            });

            // MYSTART DROPDOWN SELECTOR KEYDOWN EVENTS
            $(dropdownParent).on("keydown", dropdownSelector, function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.enter:
                    case template.KeyCodes.space:
                        e.preventDefault();

                        // IF THE DROPDOWN IS OPEN, CLOSE IT
                        if($(dropdownParent).hasClass("open")){
                            $("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                            $(this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
                        } else {
                            $(this).attr("aria-expanded", "true").parent().addClass("open").find(dropdown).attr("aria-hidden", "false").slideDown(300, "swing", function(){
                                $(dropdownList + " li:first-child a", this).attr("tabindex", "0").focus();
                            });
                        }
                    break;

                    // CONSUME TAB KEY
                    case template.KeyCodes.tab:
                        if($("+ " + dropdown + " " + dropdownList + " a").length) {
                            $("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                            $(this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                        }
                    break;

                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.down:
                    case template.KeyCodes.right:
                        e.preventDefault();

                        $("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                        $("+ " + dropdown + " " + dropdownList + " li:first-child > a", this).attr("tabindex", "0").focus();
                    break;
                }
            });

            // MYSTART DROPDOWN LINK KEYDOWN EVENTS
            $(dropdownParent).on("keydown", dropdownList + " li a", function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.left:
                    case template.KeyCodes.up:
                        e.preventDefault();

                        // IS FIRST ITEM
                        if($(this).parent().is(":first-child")) {
                            // FOCUS DROPDOWN BUTTON
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).closest(dropdownParent).find(dropdownSelector).focus();
                        } else {
                            // FOCUS PREVIOUS ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).parent().prev("li").find("> a").attr("tabindex", "0").focus();
                        }
                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case template.KeyCodes.right:
                    case template.KeyCodes.down:
                        e.preventDefault();

                        // IS LAST ITEM
                        if($(this).parent().is(":last-child")) {
                            // FOCUS FIRST ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).closest(dropdownList).find("li:first-child > a").attr("tabindex", "0").focus();
                        } else {
                            // FOCUS NEXT ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).parent().next("li").find("> a").attr("tabindex", "0").focus();
                        }
                    break;

                    // CONSUME TAB KEY
                    case template.KeyCodes.tab:
                        if(e.shiftKey) {
                            e.preventDefault();

                            // FOCUS DROPDOWN BUTTON
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).closest(dropdownParent).find(dropdownSelector).focus();
                        }
                    break;

                    // CONSUME HOME KEY
                    case template.KeyCodes.home:
                        e.preventDefault();

                        // FOCUS FIRST ITEM
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(this).closest(dropdownList).find("li:first-child > a").attr("tabindex", "0").focus();
                    break;

                    // CONSUME END KEY
                    case template.KeyCodes.end:
                        e.preventDefault();

                        // FOCUS LAST ITEM
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(this).closest(dropdownList).find("li:last-child > a").attr("tabindex", "0").focus();
                    break;
                }
            });

            $(dropdownParent).mouseleave(function() {
            	var thisDropdown = this;
            	if($(thisDropdown).find("#google_translate_element").length){
                	if(!$(thisDropdown).find("#cs-branded-translate-label").is(":focus")){
                    	$(dropdownList + " a", this).attr("tabindex", "-1");
                		$(dropdownSelector, this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
                    } else {
                    	$("#cs-branded-translate-label").on("change", function() {
                        	$(".goog-logo-link").focus();
                        })
                    }
                } else {
                	$(dropdownList + " a", this).attr("tabindex", "-1");
                	$(dropdownSelector, this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
                }

            }).focusout(function() {
                var thisDropdown = this;

                setTimeout(function () {
                	if($(thisDropdown).find("#google_translate_element").length){

                	} else {
                    	if(!$(thisDropdown).find(":focus").length) {
                        	$(dropdownSelector, thisDropdown).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                    	}
                    }

                }, 500);
            });
        },

        "Header": function() {
            // ADD LOGO
            var logoSrc = jQuery.trim("/cms/lib/VA01918722/Centricity/Template/GlobalAssets/images///logos/scps.png");
			var srcSplit = logoSrc.split("/");
			var srcSplitLen = srcSplit.length;
			if((logoSrc != "") && (srcSplit[srcSplitLen - 1] != "default-man.jpg")) {
            	if(!false) {
                	$("#gb-logo").append("
").removeClass("hidden");
                } else {
					$("#gb-logo").append("").removeClass("hidden");
                }
			} else {
            	if(!false) {
                	$("#gb-logo").append("
").removeClass("hidden");
                } else {
					$("#gb-logo").append("").removeClass("hidden");
                }
			}

            // REMOVE SITE NAME 
 IF EMPTY
            if($.trim("Spotsylvania County Public Schools") == "") {
            	$("#gb-sitename .sitename-one").remove();
                $("#gb-header").attr("data-show-school-name","false");
            }

            // REMOVE TAGELINE 
 IF EMPTY
            if($.trim("Together, we prepare our students for their future.") == "") {
            	$("#gb-sitename .sitename-tagline").remove();
            }
        },

        "ChannelBar": function() {
        	$(".sw-channel-item").unbind('hover');

            $(".sw-channel-item").hover(function(){
            	$(".sw-channel-item").off;
                $(".sw-channel-item ul").stop(true, true);
                var subList = $(this).children('ul');
                if ($.trim(subList.html()) !== "") {
                    subList.slideDown(300, "swing");
                }
                $(this).addClass("hover");
            }, function(){
                $(".sw-channel-dropdown").slideUp(300, "swing");
                $(this).removeClass("hover");
            });


            $(".sw-channel-dropdown").each(function() {
                $(this).children().wrapAll('
');
            });

        },

        "StickyChannelBar": function() {
            var navHeight = $("#gb-channel-list-outer").height();

            //ACCOUNT FOR MULTILINE CHANNEL BAR SO THAT DROPDOWNS FROM TOP LINE OF CHANNELS CAN BE REACHED
            if(navHeight > 70){
            	$("ul.sw-channel-dropdown").css("top","70px");
            }

            $(document).scroll(function() {     
                distanceFromTop = $(this).scrollTop();         

                if(distanceFromTop >= 150 && distanceFromTop < 399){
                    $("#gb-page").removeClass("sticky-header");   
                    $("#gb-page").removeClass("sticky-header-show"); 
                    $("#gb-page").removeClass("no-nav-overflow"); 
                    $("nav").height(navHeight);
                } 

                if(distanceFromTop >= 400){
                    $("nav").height(navHeight);
                    $("#gb-page").addClass("sticky-header");
                    $("#gb-page").addClass("sticky-header-show");
                    setTimeout(function () {
                        $("#gb-page").addClass("no-nav-overflow");
                    }, 500);
                } 

                if(distanceFromTop < 149){
                    $("#gb-page").removeClass("sticky-header");
                    $("nav").css("height","auto");
                }

                if($(".sp").length && $(".ui-widget.app.calendar").length) {
                    if($(".wcm-controls").hasClass("wcm-stuck")) {
                         $(".wcm-controls").css("margin-top", navHeight);
                    }
                }

                $("#sw-maincontent").css({
                    "display": "block",
                    "position": "relative",
                    "top": "-" + navHeight + "px"
                });
            });
        },

        "Body": function() {
            // FOR SCOPE
            var _this = this;

            // ADD CLASS TO NAV IF DASHBOARD IS ACTIVE
            if($("#dashboard-sidebar").length) {
